        )

mapper.select:
    	select i.customer_name, s.is_calculated, COUNT(*) as totalNum from fees_receive_storage s
		LEFT JOIN biz_instock_info i on i.fees_no=s.fees_no
		WHERE 1=1 and i.del_flag='0'

			and i.instock_no = #{instockNo}


			and i.external_num = #{externalNum}


			and i.warehouse_code = #{warehouseCode}


			and i.customer_id = #{customerId}


			and i.del_flag = #{delFlag}


			and i.create_time >= #{createTime}


			and i.create_time <= #{createEndTime}


			and s.is_calculated = #{isCalculated}


			and s.subject_code = #{subjectCode}

		GROUP BY s.is_calculated
		order by totalNum desc

    	select
    	id, fees_no as feesNo, adjust_qty as adjustQty, 
    	adjust_box as adjustBox, adjust_weight as adjustWeight,
    	last_modifier_id as lastModifierId, last_modifier as lastModifier, 
    	last_modify_time as lastModifyTime, del_flag as delFlag, 
    	is_calculated as isCalculated, calculate_time as calculateTime, remark,
        instock_no as instockNo, external_num as externalNum, 
        warehouse_code as warehouseCode, warehouse_name as warehouseName, 
        customer_id as customerId, customer_name as customerName, 
        instock_type as instockType, total_qty as totalQty, total_box as totalBox, 
        total_weight as totalWeight, receiver, creator, create_time as createTime, write_time as writeTime
		from biz_instock_info
		where del_flag='0'

			and create_time  >=  #{beginTime,jdbcType=VARCHAR}


			and create_time  <=  #{endTime,jdbcType=VARCHAR}


			and customer_id = #{customerId,jdbcType=VARCHAR}


			and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}


			and id = #{id,jdbcType=BIGINT}


			and is_calculated = concat(${isCalculated})

		LIMIT #{num}

mapper.update:
update biz_instock_info
is_calculated=#{isCalculated,jdbcType=VARCHAR}, fees_no=#{feesNo,jdbcType=VARCHAR}, remark=#{calcuMsg,jdbcType=VARCHAR}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP},
where fees_no=#{feesNo}

update biz_instock_info
is_calculated=1, fees_no=#{feesNo,jdbcType=VARCHAR}, calculate_time=#{bizCalculateTime,jdbcType=TIMESTAMP},
where id=#{bId}
