mapper.select:
        select
        id, outstock_no, customerid,customer_name, carrier_id,carrier_name, deliverid, deliver_name, waybill_no, waybill_num, waybill_list, 
         IFNULL(total_weight,0) as total_weight,IFNULL(adjust_weight,0) as adjust_weight, warehouse_code,warehouse_name, service_type_code, dispatch_type_code, 
        collect_money, month_fee_count, is_calculated, send_name, send_province_id, 
        send_city_id, send_district_id, send_street, send_detail_address, 
        receive_name, receive_province_id, receive_city_id, receive_district_id, 
        receive_street, receive_detail_address, receive_time, fees_no, account_state,external_no,sign_time,accept_time, 
        del_flag, total_volume,create_time,temperature_type_code,temperature_type_name,remark,zexpressnum, bigstatus, smallstatus, system_weight, totalqty, product_detail, expresstype, sku_num, extattr1, extattr2, extattr3, extattr4, extattr5,adjust_deliver_id,
        adjust_deliver_name, adjust_carrier_id, adjust_carrier_name,adjust_province_id,adjust_city_id,
        adjust_district_id,origin_carrier_id,origin_carrier_name,IFNULL(throw_weight,0) as throw_weight,order_status,origin_weight,
        adjust_service_type_code as adjustServiceTypeCode
        from biz_dispatch_bill
        where del_flag = 0

			and create_time  >=  #{beginTime,jdbcType=VARCHAR}


			and create_time  <=  #{endTime,jdbcType=VARCHAR}


			and customerid = #{customerId,jdbcType=VARCHAR}


			and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}


			and carrier_id = #{carrierId,jdbcType=VARCHAR}


			and deliverid = #{deliveryId,jdbcType=VARCHAR}


			and id = #{id,jdbcType=BIGINT}


			and is_calculated = concat(${isCalculated})


			and waybill_no = #{waybillNo,jdbcType=VARCHAR}

		LIMIT #{num}

mapper.update:
update biz_dispatch_bill
fees_no=#{feesNo,jdbcType=VARCHAR}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP}, origin_carrier_name=#{originCarrierName,jdbcType=VARCHAR}, origin_carrier_id=#{originCarrierId,jdbcType=VARCHAR}, correct_throw_weight=#{correctThrowWeight,jdbcType=DECIMAL}
where id=#{id}


	    update biz_dispatch_bill




                      when id=#{biz.id} then #{biz.feesNo}






                       when id=#{biz.id} then #{biz.isCalculated}






                      when id=#{biz.id} then #{biz.remark}






                      when id=#{biz.id} then #{biz.calculateTime}






                      when id=#{biz.id} then #{biz.originCarrierName}






                      when id=#{biz.id} then #{biz.originCarrierId}






                      when id=#{biz.id} then #{biz.correctThrowWeight}




	    where id in

            #{biz.id,jdbcType=BIGINT}

mapper.select:
select   from biz_dispatch_carrier_change where waybill_no=#{waybillNo} and new_carrier_id=#{carrierId}
and warehouse_code=#{warehouseCode,jdbcType=VARCHAR}
and customerid=#{customerid,jdbcType=VARCHAR}

mapper.update:
update biz_dispatch_carrier_change
warehouse_code=#{warehouseCode,jdbcType=VARCHAR}, customerid=#{customerid,jdbcType=VARCHAR}, old_carrier_id=#{oldCarrierId,jdbcType=VARCHAR}, new_carrier_id=#{newCarrierId,jdbcType=VARCHAR}, waybill_no=#{waybillNo,jdbcType=VARCHAR}, update_reason_type_code=#{updateReasonTypeCode,jdbcType=VARCHAR}, update_reason_type=#{updateReasonType,jdbcType=VARCHAR}, update_reason_detail_code=#{updateReasonDetailCode,jdbcType=VARCHAR}, update_reason_detail=#{updateReasonDetail,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, write_time=#{writeTime,jdbcType=TIMESTAMP}, duty_type=#{dutyType,jdbcType=VARCHAR}, old_update_reason=#{oldUpdateReason,jdbcType=VARCHAR},
where id=#{id}
