mapper.update:
update bms_asyn_calcu_task
task_status=#{taskStatus,jdbcType=TINYINT}, fees_count=#{feesCount,jdbcType=INTEGER}, uncalcu_count=#{uncalcuCount,jdbcType=INTEGER}, calcu_count=#{calcuCount,jdbcType=INTEGER}, begin_count=#{beginCount,jdbcType=INTEGER}, finish_count=#{finishCount,jdbcType=INTEGER}, sys_error_count=#{sysErrorCount,jdbcType=INTEGER}, contract_miss_count=#{contractMissCount,jdbcType=INTEGER}, quote_miss_count=#{quoteMissCount,jdbcType=INTEGER}, no_exe_count=#{noExeCount,jdbcType=INTEGER}, task_rate=#{taskRate,jdbcType=INTEGER}, process_time=#{processTime,jdbcType=TIMESTAMP}, finish_time=#{finishTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR},
where task_id=#{taskId}

mapper.select:
        select a.customer_id as customerId,a.customer_name as customerName,a.subject_code as subjectCode,b.subject_name as subjectName, DATE_FORMAT(a.create_time,'%Y%m') as creMonth
		from fees_receive_storage a 
    	LEFT JOIN bms_subject_info b on a.subject_code=b.subject_code and b.in_out_typecode='INPUT' and b.del_flag='0'
   		where a.is_calculated=#{isCalculated}

			and a.subject_code in

				#{item}


		GROUP BY a.customer_id,a.subject_code,DATE_FORMAT(a.create_time,'%Y%m');

        select customerid as customerId,subject_code as subjectCode,DATE_FORMAT(create_time,'%Y%m') as creMonth
		from fees_receive_dispatch
		where is_calculated=#{isCalculated}
		and del_flag='0'

			and subject_code in

				#{item}


		GROUP BY customerid,subject_code,DATE_FORMAT(create_time,'%Y%m');
