mapper.select:
    	 select

        from bms_subject_info
        where ifnull(del_flag,0)=0

        	and in_out_typecode = #{inOutTypeCode}


			and biz_typecode = #{bizTypeCode}


			and fees_type = #{feesType}


			and dimen1_code=#{dimen1Code} 


			and dimen2_code=#{dimen2Code}

		order by dimen1_sortno asc

select

   from bms_subject_info
   where ifnull(del_flag,0)=0 and in_out_typecode = #{inOutTypeCode} and biz_typecode = #{bizTypeCode} and subject_code=#{subjectCode}

mapper.update:
update bms_subject_info
in_out_typecode=#{inOutTypecode,jdbcType=VARCHAR}, biz_typecode=#{bizTypecode,jdbcType=VARCHAR}, fees_type=#{feesType,jdbcType=VARCHAR}, subject_name=#{subjectName,jdbcType=VARCHAR}, subject_code=#{subjectCode,jdbcType=VARCHAR}, dimen1_code=#{dimen1Code,jdbcType=VARCHAR}, dimen1_sortno=#{dimen1Sortno,jdbcType=INTEGER}, dimen2_code=#{dimen2Code,jdbcType=VARCHAR}, dimen2_sortno=#{dimen2Sortno,jdbcType=INTEGER}, dimen3_code=#{dimen3Code,jdbcType=VARCHAR}, dimen3_sortno=#{dimen3Sortno,jdbcType=INTEGER}, dimen4_code=#{dimen4Code,jdbcType=VARCHAR}, dimen4_sortno=#{dimen4Sortno,jdbcType=INTEGER}, dimen5_code=#{dimen5Code,jdbcType=VARCHAR}, dimen5_sortno=#{dimen5Sortno,jdbcType=INTEGER}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}
where id=#{id}

mapper.select:
    	select 
        from bms_subject_info
        where 1=1

				and in_out_typecode = #{inOutTypecode}


				and biz_typecode = #{bizTypecode}


				and fees_type = #{feesType}


				and subject_name = #{subjectName}


				and subject_code = #{subjectCode}

		and fees_type!='APP' and del_flag='0'
