mapper.select:
    	select  

        from file_export_task
        where 1=1 and del_flag = '0'

			and task_name like concat('%',#{taskName},'%') 


			and task_type = #{taskType}


			and task_state = #{taskState}


			and creator like concat('%',#{creator},'%')


			and customerid = #{customerId}


			and start_time>=#{startDate}


			and end_time <=#{endDate}

        order by create_time desc

mapper.update:
update file_export_task
task_id=#{taskId,jdbcType=VARCHAR}, task_name=#{taskName,jdbcType=VARCHAR}, bill_no=#{billNo,jdbcType=VARCHAR}, customerid=#{customerid,jdbcType=VARCHAR}, start_time=#{startTime,jdbcType=TIMESTAMP}, end_time=#{endTime,jdbcType=TIMESTAMP}, task_type=#{taskType,jdbcType=VARCHAR}, task_state=#{taskState,jdbcType=VARCHAR}, progress=#{progress,jdbcType=DOUBLE}, file_path=#{filePath,jdbcType=VARCHAR}, param1=#{param1,jdbcType=VARCHAR}, param2=#{param2,jdbcType=VARCHAR}, param3=#{param3,jdbcType=VARCHAR}, param4=#{param4,jdbcType=VARCHAR}, param5=#{param5,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR},
where task_id = #{taskId,jdbcType=VARCHAR}

mapper.select:
select count(*) from file_export_task
where customerid=#{customerId} and start_time=#{startTime} and end_time=#{endTime}
and task_type=#{taskType}
