mapper.select:
select id, group_id, customerid, sort_no, creator, create_time, last_modifier, last_modify_time, del_flag
from bms_group_customer
where 1=1 and ifnull(del_flag,'0')='0'

	and group_id in

		#{item}



		and group_id = #{groupId}

select customerid from bms_group_customer where group_id=#{groupId} and ifnull(del_flag,'0')='0' and
customerid in

	#{item}

mapper.update:
update bms_group_customer
group_id=#{groupId,jdbcType=INTEGER}, customerid=#{customerid,jdbcType=VARCHAR}, sort_no=#{sortNo,jdbcType=INTEGER}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR},
where id=#{id}
