mapper.select:
select area_code as areaCode from bms_group_user
where del_flag='0' 
and user_id=#{userId}
and id != #{id}
limit 1

select a.id,a.group_id,a.user_id,a.user_name,a.administrator,
a.creator,a.create_time,a.last_modifier,a.last_modify_time,b.group_name
from bms_group_user a
left join bms_group b on a.group_id=b.id
where 1=1

	and group_id=#{groupId}


	and user_id=#{userId}


	and user_name like CONCAT('%',#{userName},'%')


	and b.biz_type = #{bizType}

mapper.update:
update bms_group_user
group_id=#{groupId}, user_id=#{userId}, user_name=#{userName}, area_code=#{areaCode}, administrator=#{administrator}, creator=#{creator}, create_time=#{createTime}, last_modifier=#{lastModifier}, last_modify_time=#{lastModifyTime}, del_flag=#{delFlag},
where id=#{id}

mapper.select:
select id,group_id,user_id,user_name,area_code, creator,create_time,last_modifier,last_modify_time,del_flag from bms_group_user where 1=1
and user_id=#{userId}
and user_name like CONCAT('%',#{userName},'%')
and del_flag=#{delFlag}

select id,group_id,user_id,user_name,area_code, creator,create_time,last_modifier,last_modify_time,del_flag from bms_group_user WHERE 1=1 and group_id in (select id from bms_group WHERE biz_type='sale_area')
and user_id=#{userId}
and user_name like CONCAT('%',#{userName},'%')
and del_flag=#{delFlag}
