mapper.select:
        select
        id, create_month, invoice_name, invoice_id, bill_name, task_rate, task_status, 
        IFNULL(amount,0) as amount, IFNULL(adjust_amount,0) as adjustAmount, SUM(IFNULL(amount,0)+IFNULL(adjust_amount,0)) as totalAmount, 
        creator, creator_id, create_time, finish_time, 
        last_modifier, last_modifier_id, last_modify_time, del_flag, bill_no, 
        origin_file_name, origin_file_path, result_file_name, result_file_path
        from bill_receive_master
        where 1 = 1

				and id = #{id}


				and create_month = #{createMonth}


				and create_month >= #{createMonthStart}


				and create_month <= #{createMonthEnd}


				and invoice_name = #{invoiceName}


				and invoice_id = #{invoiceId}


				and bill_name like CONCAT('%',#{billName},'%')


				and task_rate = #{taskRate}


				and task_status = #{taskStatus}


				and amount = #{amount}


				and adjust_amount = #{adjustAmount}


				and creator like CONCAT('%',#{creator},'%')


				and creator_id = #{creatorId}


				and create_time >= #{createTime}


				and create_time <= #{createEndTime}


				and del_flag = #{delFlag}


				and bill_no = #{billNo}

			GROUP BY bill_no
