mapper.select:
        select a.id, a.bill_check_id, a.sort_no, a.invoice_no, a.invoice_amount, a.invoice_date, a.waybill_no, a.invoice_days, a.remark, a.creator_id,a.creator, a.create_time, a.last_modifier, a.last_modify_time,a.del_flag,
		b.create_month,b.invoice_name,b.bill_name,b.seller_name,b.area
		from bill_check_invoice a inner JOIN bill_check_info b on a.bill_check_id=b.id
        and a.del_flag='0' and b.del_flag='0' and b.bill_status not in ('INVALIDATE','BAD_BILL')

				and a.invoice_no like '%${invoiceNo}%'


				and a.invoice_date = #{invoiceDate,jdbcType=DATE}


				and a.invoice_date >= #{invoiceDateStart,jdbcType=DATE}


				and a.invoice_date <= #{invoiceDateEnd,jdbcType=DATE}


				and b.create_month >= #{createMonthStart}


				and b.create_month <= #{createMonthEnd}


				and a.waybill_no like '%${waybillNo}%'


				and b.create_month = #{createMonth}


				and b.invoice_name  like '%${invoiceName}%'


				and b.bill_name like '%${billName}%'


				and b.seller_name like '%${sellerName}%'


				and b.area like '%${area}%'

        select sum(a.invoice_amount) as totalPrice
		from bill_check_invoice a inner JOIN bill_check_info b on a.bill_check_id=b.id
        and a.del_flag='0' and b.del_flag='0' and b.bill_status not in ('INVALIDATE','BAD_BILL')

				and a.invoice_no like '%${invoiceNo}%'


				and a.invoice_date = #{invoiceDate,jdbcType=DATE}


				and a.invoice_date >= #{invoiceDateStart,jdbcType=DATE}


				and a.invoice_date <= #{invoiceDateEnd,jdbcType=DATE}


				and a.waybill_no like '%${waybillNo}%'


				and b.create_month = #{createMonth}


				and b.create_month >= #{createMonthStart}


				and b.create_month <= #{createMonthEnd}


				and b.invoice_name  like '%${invoiceName}%'


				and b.bill_name like '%${billName}%'


				and b.seller_name like '%${sellerName}%'


				and b.area like '%${area}%'

mapper.update:
update bill_check_invoice
bill_check_id=#{billCheckId,jdbcType=INTEGER}, sort_no=#{sortNo,jdbcType=INTEGER}, invoice_no=#{invoiceNo,jdbcType=VARCHAR}, invoice_amount=#{invoiceAmount,jdbcType=DECIMAL}, invoice_date=#{invoiceDate,jdbcType=DATE}, waybill_no=#{waybillNo,jdbcType=VARCHAR}, invoice_days=#{invoiceDays,jdbcType=INTEGER}, remark=#{remark,jdbcType=VARCHAR}, creator_id=#{creatorId,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, mk_invoice_name=#{mkInvoiceName,jdbcType=VARCHAR},
where id=#{id}

