mapper.select:
    	SELECT * from biz_dispatch_pay_bill where del_flag='0' 

			    and create_time>= #{createTime}


			    and create_time <= #{endTime} 


				and deliverid = #{deliverid}

    	 and is_calculated not in ('1','5') limit 1

