mapper.select:
        select a.id,a.wms_id, a.operation_time, a.warehouse_code, a.warehouse_name, 
		a.customerid, a.customer_name, a.num, a.fees_no, a.fees_type,a.fees_type_name, 
		a.fees_unit, a.item, s.is_calculated, a.creator, a.create_time, a.last_modifier, 
		a.last_modify_time, a.del_flag, a.calculate_time, a.write_time, a.adjust_num, s.calcu_msg as remark, 
		a.external_no,a.service_content,a.remark_content, a.pay_no,a.fixed_amount,a.first_subject,a.first_subject_name,
		s.cost as cost, s.unit_price as unitPrice from biz_add_fee a
		LEFT JOIN fees_receive_storage s on a.fees_no=s.fees_no and s.del_flag='0'
		WHERE 1=1

				and a.id = #{id}


			    and a.create_time >= #{startTime} and  a.create_time <= #{endTime}


				and a.warehouse_code = #{warehouseCode}


				and a.warehouse_name = #{warehouseName}


				and a.customerid = #{customerid}


				and a.customer_name = #{customerName}


				and a.num = #{num}


				and a.fees_no = #{feesNo}


				and a.fees_type = #{feesType}


				and a.fees_unit = #{feesUnit}


				and a.item = #{item}


				and s.is_calculated = #{isCalculated}


				and a.creator = #{creator}


			    and a.create_time >= #{createTime} add a.create_time <= #{createTime}


				and a.last_modifier = #{lastModifier}


			    and a.last_modify_time >= #{lastModifyTime} add a.last_modify_time <= #{lastModifyTime}


				and a.del_flag = #{delFlag}


			    and a.calculate_time >= #{calculateTime} add a.calculate_time <= #{calculateTime}


			    and a.write_time >= #{writeTime} add a.write_time <= #{writeTime}


				and a.adjust_num = #{adjustNum}


				and a.first_subject = #{firstSubject}


				and a.pay_no = #{payNo}

mapper.update:
update biz_add_fee
operation_time=#{operationTime,jdbcType=TIMESTAMP}, warehouse_code=#{warehouseCode,jdbcType=VARCHAR}, warehouse_name=#{warehouseName,jdbcType=VARCHAR}, customerid=#{customerid,jdbcType=VARCHAR}, customer_name=#{customerName,jdbcType=VARCHAR}, num=#{num,jdbcType=DOUBLE}, fees_no=#{feesNo,jdbcType=VARCHAR}, fees_type=#{feesType,jdbcType=VARCHAR}, fees_unit=#{feesUnit,jdbcType=VARCHAR}, item=#{item,jdbcType=VARCHAR}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP}, write_time=#{writeTime,jdbcType=TIMESTAMP}, adjust_num=#{adjustNum,jdbcType=DOUBLE}, remark=#{remark}, external_no=#{externalNo,jdbcType=VARCHAR}, service_content=#{serviceContent,jdbcType=VARCHAR}, remark_content=#{remarkContent,jdbcType=VARCHAR}
where 1=1
and operation_time=#{operationTime,jdbcType=VARCHAR}
and warehouse_code=#{warehouseCode,jdbcType=VARCHAR}
and customerid=#{customerid,jdbcType=VARCHAR}
and item=#{item,jdbcType=VARCHAR}
and id=#{id}
and wms_id=#{wmsId}

update biz_add_fee
last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, adjust_num=#{adjustNum,jdbcType=DOUBLE}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, fees_unit=#{feesUnit,jdbcType=VARCHAR}, remark=#{remark} external_no=#{externalNo,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
select   
from  biz_add_fee 
where 1=1 and del_flag='0'
and operation_time=#{operationTime,jdbcType=TIMESTAMP}  
and warehouse_code=#{warehouseCode,jdbcType=VARCHAR}
and customerid=#{customerid,jdbcType=VARCHAR}
and fees_type=#{feesType,jdbcType=VARCHAR}
 and external_no=#{externalNo,jdbcType=VARCHAR} 
limit 1

    	select a.id, a.wms_id, a.operation_time, a.warehouse_code,
		a.warehouse_name, a.customerid, a.customer_name,a.num, a.fees_no,
		a.fees_type, a.fees_unit, a.item, a.is_calculated, a.creator,
		a.create_time, a.last_modifier, a.last_modify_time, a.del_flag,
		a.calculate_time, a.write_time, a.adjust_num, a.fees_type_name, a.remark,
		a.external_no
		from biz_add_fee a
		where a.del_flag = 0 

			and customerid = #{customerId,jdbcType=VARCHAR}


			and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}


			and id = #{id,jdbcType=BIGINT}


			and is_calculated = concat(${isCalculated})

		LIMIT #{num}

    	select a.customer_name, a.fees_type_name, 
		s.is_calculated, COUNT(*) as totalNum
		from fees_receive_storage s
		LEFT JOIN biz_add_fee a on a.fees_no=s.fees_no and a.del_flag='0'
		WHERE 1=1

			and a.warehouse_code = #{warehouseCode}


			and a.customerid = #{customerid}


			and a.create_time >= #{startTime}


			and a.create_time <= #{endTime}


			and s.is_calculated = #{isCalculated}


			and a.fees_type = #{feesType}

		GROUP BY a.customer_name, s.is_calculated, a.fees_type_name
		ORDER BY totalNum DESC

mapper.update:
    	UPDATE fees_receive_storage s
		LEFT JOIN biz_add_fee a on s.fees_no=a.fees_no and a.del_flag='0'
		SET s.is_calculated='99', s.calcu_msg=''
		WHERE 1=1 and s.del_flag='0' and a.fixed_amount<=0

			and a.customerid = #{customerid}


			and a.warehouse_code = #{warehouseCode}


			and a.create_time >= #{startTime}


			and a.create_time <= #{endTime}


			and a.fees_type = #{feesType}


			and s.is_calculated = #{isCalculated}


		    and a.pay_no = #{payNo}

mapper.select:
    	select 
		a.customerid,s.subject_code as subjectCode, DATE_FORMAT(a.create_time,'%Y%m') as creMonth  from biz_add_fee a
		LEFT JOIN fees_receive_storage s on a.fees_no=s.fees_no  
		WHERE 1=1 and a.del_flag='0' and s.del_flag='0'

				and a.id = #{id}


			    and a.create_time >= #{startTime} and  a.create_time <= #{endTime}


				and a.warehouse_code = #{warehouseCode}


				and a.warehouse_name = #{warehouseName}


				and a.customerid = #{customerid}


				and a.customer_name = #{customerName}


				and a.num = #{num}


				and a.fees_no = #{feesNo}


				and a.fees_type = #{feesType}


				and a.fees_unit = #{feesUnit}


				and a.item = #{item}


				and s.is_calculated = #{isCalculated}


				and a.creator = #{creator}


			and a.create_time >= #{startTime}


			and a.create_time <= #{endTime}


				and a.last_modifier = #{lastModifier}


				and a.adjust_num = #{adjustNum}

    	group by a.customerid,s.subject_code, DATE_FORMAT(a.create_time,'%Y%m')

mapper.update:
        update biz_add_fee
		set del_flag='0',is_calculated='0'
		where del_flag='4'
		and create_time>=#{beginTime} and  create_time<#{endTime} 
		and customerid=#{customerId}
