mapper.update:
update biz_product_storage
wms_id=#{wmsId,jdbcType=VARCHAR}, data_num=#{dataNum,jdbcType=VARCHAR}, cur_day=#{curDay,jdbcType=VARCHAR}, cur_time=#{curTime,jdbcType=TIMESTAMP}, warehouse_code=#{warehouseCode,jdbcType=VARCHAR}, warehouse_name=#{warehouseName,jdbcType=VARCHAR}, customerid=#{customerid,jdbcType=VARCHAR}, customer_name=#{customerName,jdbcType=VARCHAR}, product_id=#{productId,jdbcType=VARCHAR}, product_name=#{productName,jdbcType=VARCHAR}, stock_place_code=#{stockPlaceCode,jdbcType=VARCHAR}, stock_place=#{stockPlace,jdbcType=VARCHAR}, batch_code=#{batchCode,jdbcType=VARCHAR}, aqty=#{aqty,jdbcType=DOUBLE}, product_date=#{productDate,jdbcType=TIMESTAMP}, expiry_date=#{expiryDate,jdbcType=TIMESTAMP}, in_time=#{inTime,jdbcType=TIMESTAMP}, fees_no=#{feesNo,jdbcType=VARCHAR}, dbname=#{dbname,jdbcType=VARCHAR}, temperature=#{temperature,jdbcType=VARCHAR}, weight=#{weight,jdbcType=DOUBLE}, volume=#{volume,jdbcType=DOUBLE}, pallet_num=#{palletNum,jdbcType=DOUBLE}, piece_num=#{pieceNum,jdbcType=DOUBLE}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, write_time=#{writeTime,jdbcType=TIMESTAMP}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
    	SELECT * from biz_product_storage where del_flag='0' 

			    and create_time>= #{createTime}


			    and create_time <= #{endTime} 


				and customerid = #{customerid}

    	 and is_calculated not in ('1','5') limit 1

        select a.data_num from biz_product_storage a
		inner join fees_receive_storage b on a.fees_no = b.fees_no
		inner join fees_bill c on b.bill_no = c.billno and c.delflag = 0
		where a.del_flag = 0 and b.del_flag = 0 

				and a.warehouse_code = #{warehouseCode}


				and a.warehouse_name = #{warehouseName}


				and a.customerid = #{customerid}


				and a.customer_name = #{customerName}


			    and a.cur_time >= #{startTime} 


			    and a.cur_time <= #{endTime}


				and a.extattr1 = #{extattr1}


				and a.is_calculated = #{isCalculated}

		LIMIT 1

mapper.update:
        update fees_receive_storage b
 		LEFT JOIN biz_product_storage a on a.fees_no=b.fees_no and a.del_flag='0' 
 		set b.is_calculated = '99', b.calcu_msg=''
 		where 1=1 and b.del_flag='0'

			and a.customerid = #{customerid}


			and a.create_time >= #{startTime} 



			and a.create_time <= #{endTime}

mapper.select:
    	select 
		a.customerid,b.subject_code as subjectCode, DATE_FORMAT(a.create_time,'%Y%m') as creMonth  from biz_product_storage a
		LEFT JOIN fees_receive_storage b on a.fees_no=b.fees_no
		WHERE b.del_flag='0' and a.del_flag='0' and a.is_calculated != 0 and b.is_calculated = '99'

				and a.id = #{id}


				and a.wms_id = #{wmsId}


				and a.data_num = #{dataNum}


				and a.cur_day = #{curDay}


			    and a.cur_time >= #{curTime} add a.cur_time <= #{curTime}


				and a.warehouse_code = #{warehouseCode}


				and a.warehouse_name = #{warehouseName}


				and a.customerid = #{customerid}


				and a.customer_name = #{customerName}


				and a.product_id = #{productId}


				and a.product_name = #{productName}


				and a.stock_place_code = #{stockPlaceCode}


				and a.stock_place = #{stockPlace}


				and a.batch_code = #{batchCode}


				and a.aqty = #{aqty}


			    and a.product_date >= #{productDate} add a.product_date <= #{productDate}


			    and a.expiry_date >= #{expiryDate} add a.expiry_date <= #{expiryDate}


			    and a.in_time >= #{inTime} add a.in_time <= #{inTime}


				and a.fees_no = #{feesNo}


				and a.dbname = #{dbname}


				and a.temperature = #{temperature}


				and a.weight = #{weight}


				and a.volume = #{volume}


				and a.pallet_num = #{palletNum}


				and a.piece_num = #{pieceNum}


				and a.remark = #{remark}


				and a.creator = #{creator}


				and a.last_modifier = #{lastModifier}


			    and a.last_modify_time >= #{lastModifyTime} add a.last_modify_time <= #{lastModifyTime}


			    and a.cur_time >= #{startTime} 



			    and a.cur_time <= #{endTime}


				and a.extattr1 = #{extattr1}

    	group by a.customerid,b.subject_code, DATE_FORMAT(a.create_time,'%Y%m')

mapper.update:
   update biz_product_storage
   set del_flag='0',is_calculated='0'
   where del_flag='4'
   and create_time>=#{beginTime} and  create_time<#{endTime}
	  and customerid=#{customerId}
