mapper.update:
    	update biz_dispatch_bill a inner JOIN fees_receive_dispatch_discount b on a.waybill_no=b.waybill_no set b.is_calculated='0',b.task_id=#{taskId}
		where a.create_time >= #{startTime}
		and a.create_time<#{endTime}
		and a.customerid = #{customerId}
		and a.del_flag = 0 and a.carrier_id=#{carrierId} and a.is_calculated != '5'
