mapper.update:
update fees_receive_dispatch_discount
task_id=#{taskId,jdbcType=VARCHAR}, subject_code=#{subjectCode,jdbcType=VARCHAR}, fees_no=#{feesNo,jdbcType=VARCHAR}, waybill_no=#{waybillNo,jdbcType=VARCHAR}, unit_rate=#{unitRate,jdbcType=DECIMAL}, unit_price=#{unitPrice,jdbcType=DECIMAL}, first_rate=#{firstRate,jdbcType=DECIMAL}, first_price=#{firstPrice,jdbcType=DECIMAL}, continue_rate=#{continueRate,jdbcType=DECIMAL}, continue_price=#{continuePrice,jdbcType=DECIMAL}, discount_amount=#{discountAmount,jdbcType=DECIMAL}, derate_amount=#{derateAmount,jdbcType=DECIMAL}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP}, quote_id=#{quoteId,jdbcType=BIGINT}, write_time=#{writeTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR},
where id=#{id}

update fees_receive_storage_discount
task_id=#{taskId,jdbcType=VARCHAR}, subject_code=#{subjectCode,jdbcType=VARCHAR}, fees_no=#{feesNo,jdbcType=VARCHAR}, waybill_no=#{waybillNo,jdbcType=VARCHAR}, unit_rate=#{unitRate,jdbcType=DECIMAL}, unit_price=#{unitPrice,jdbcType=DECIMAL}, first_rate=#{firstRate,jdbcType=DECIMAL}, first_price=#{firstPrice,jdbcType=DECIMAL}, continue_rate=#{continueRate,jdbcType=DECIMAL}, continue_price=#{continuePrice,jdbcType=DECIMAL}, discount_amount=#{discountAmount,jdbcType=DECIMAL}, derate_amount=#{derateAmount,jdbcType=DECIMAL}, is_calculated=#{isCalculated,jdbcType=VARCHAR}, calculate_time=#{calculateTime,jdbcType=TIMESTAMP}, quote_id=#{quoteId,jdbcType=BIGINT}, write_time=#{writeTime,jdbcType=TIMESTAMP}, remark=#{remark,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
       select 
			a.customerid as customerId,
			a.carrier_id as carrierId,
			count(0) as orderCount, 
			sum(b.amount) as amount
		from biz_dispatch_bill a
		left join fees_receive_dispatch b on a.fees_no = b.fees_no and b.del_flag=0
		where a.create_time >= #{startTime}
		and a.create_time<#{endTime}
		and a.customerid = #{customerId} and a.carrier_id =#{carrierId}
		and a.del_flag = 0 and a.is_calculated in ('1')

			and a.service_type_code=#{serviceTypeCode}


			and a.adjust_service_type_code=#{adjustServiceTypeCode}

		group by a.customerid,a.carrier_id

mapper.update:
    	update biz_dispatch_bill a 
    	inner JOIN fees_receive_dispatch_discount b on a.waybill_no=b.waybill_no 
    	inner join fees_receive_dispatch c on a.waybill_no = c.waybill_no set b.is_calculated='0',b.task_id=#{taskId}
		where a.create_time >= #{startTime}
		and a.create_time<#{endTime}
		and a.customerid = #{customerId}
		and a.del_flag = 0 and c.carrierid=#{carrierId} and a.is_calculated in ('1')

    	update biz_dispatch_bill a 
    	inner JOIN fees_receive_dispatch_discount b on a.waybill_no=b.waybill_no 
    	inner join fees_receive_dispatch c on a.waybill_no = c.waybill_no set b.is_calculated='0',b.task_id=#{taskId}
		where a.create_time >= #{startTime}
		and a.create_time<#{endTime}
		and a.customerid = #{customerId}
		and a.del_flag = 0 and c.carrierid=#{carrierId} and a.is_calculated != '5'

mapper.delete:
    	delete from fees_receive_storage_discount where fees_no in(
			select fees_no from fees_receive_storage
			where 1=1 and del_flag = '0'
	        and customer_id=#{customerId} 
	    	and create_time>=#{startTime} and create_time<#{endTime}
	    	and subject_code=#{subjectCode}
			and is_calculated in ('1')

				and tempreture_type in ('LD','LC')

		)

mapper.insert:
    	INSERT INTO fees_receive_storage_discount(task_id,subject_code,fees_no,waybill_no,is_calculated)
		select #{taskId},subject_code,fees_no,waybill_no,'0' from fees_receive_storage
		where 1=1 and del_flag = '0'
        and customer_id=#{customerId} 
    	and create_time>=#{startTime} and create_time<#{endTime}
    	and subject_code=#{subjectCode}
		and is_calculated in ('1')

			and tempreture_type in ('LD','LC')
