        SELECT  abn.id,abn.fee_no,abn.reference,abn.expressnum,abn.order_no,abn.reason,abn.reason_detail,abn.carrier_name,abn.deliver_name,abn.customer_name,
        abn.create_person_name,abn.create_time,abn.bill_no,
			case abn.ispay
			 when '0' then '否'
			 when '1' then '是'
			end as ispayStr,
			(case when abn.reason_id=4 then abn.pay_money else -abn.pay_money end) pay_money,
			case abn.is_calculated
			  when '0' then '未计算'
			  when '1' then '已计算'
			end as is_calculated,
			case abn.is_conflict
			when '0' then '否'
			 when    '1' then '是'
			end as isConflictStr,abn.remark,abn.warehouse_id,abn.customer_id,b.amount as delivery_cost,
			IF((ISNULL(is_delivery_free) || LENGTH(trim(is_delivery_free))<1),'0',is_delivery_free)
			as isDeliveryFree	
		 from  fees_abnormal abn 
		 left join fees_receive_dispatch b
             on abn.expressnum=b.waybill_no and b.del_flag='0' and b.subject_code<>'de_abnormal_pay'
	 	 where 1=1

				and abn.id = #{id}


				and abn.fee_no = #{feeNo}


				and abn.reference = #{reference}


				and abn.expressnum = #{expressnum}


				and abn.order_no = #{orderNo}


				and abn.reason_id = #{reasonId}


				and abn.reason = #{reason}


				and abn.reason_detail = #{reasonDetail}



			 	and abn.customer_id in

			 		#{item}



				and abn.customer_id = #{customerId}


				and abn.carrier_id = #{carrierId}


				and abn.deliver_id = #{deliverId}


				and abn.warehouse_id = #{warehouseId}


				and abn.ispay = #{ispay}


			    and abn.create_time >= #{startTime} and  abn.create_time <= #{endTime}


				and abn.reason_id in (1,2,3,4,56)


				and abn.reason_id in (2,4)


				and abn.pay_money = #{payMoney}


				and abn.is_conflict = #{isConflict}


