mapper.select:
   SELECT  a.id,a.fee_no,a.reference,a.expressnum,a.order_no,a.reason,a.reason_detail,a.customer_id,a.customer_name,
    		a.carrier_id,a.carrier_name,a.deliver_id,a.deliver_name,a.warehouse_id,a.warehouse_name,
    		a.bill_no,a.create_person_name,a.create_time,a.reason_id,
			case a.ispay
			 when '0' then '否'
			 when '1' then '是'
			end as ispayStr,
			ifnull((case when a.reason_id=4 then a.pay_money else -a.pay_money end),0) pay_money,a.is_delivery_free,ifnull(b.amount,0) as delivery_cost,
			case a.is_calculated
			  when '0' then '未计算'
			  when '1' then '已计算'
			end as is_calculated,
			case a.is_conflict
			when '0' then '否'
			 when    '1' then '是'
			end as isConflictStr,a.remark,ifnull(a.derate_amount,0) as derate_amount
		from  fees_abnormal  a
		left join fees_receive_dispatch b
		on a.expressnum=b.waybill_no and b.del_flag='0' and b.subject_code<>'de_abnormal_pay'
		where a.warehouse_id=#{warehouseCode}

			and a.reason_id in

				#{item}



			and a.fee_no in

				#{feesno}



			and a.expressnum=#{waybillno}


			and a.create_time>=#{startTime}


			and a.create_time<=#{endTime}


				and a.bill_no = #{billNo}


				and a.is_calculated = #{isCalculated}

SELECT id,fee_no,reference,expressnum,order_no,reason,reason_detail,customer_id,customer_name, carrier_id,carrier_name,deliver_id,deliver_name,warehouse_id,warehouse_name, bill_no,create_person_name,create_time, case ispay when '0' then '否' when '1' then '是' end as ispayStr, (case when reason_id=4 then pay_money else -pay_money end) pay_money, IF((ISNULL(is_delivery_free) || LENGTH(trim(is_delivery_free))<1),'0',is_delivery_free) as isDeliveryFree, delivery_cost, case is_calculated when '0' then '未计算' when '1' then '已计算' end as is_calculated, case is_conflict when '0' then '否' when '1' then '是' end as isConflictStr,remark,warehouse_id from fees_abnormal where 1=1 and is_calculated = '0' and reason_id in('1','3','4','56') and customer_id = #{customerId}
and expressnum = #{expressnum}
and fee_no = #{feeNo}
and create_time >= #{operatorStartTime}
and create_time <= #{operatorEndTime}

SELECT id,fee_no,reference,expressnum,order_no,reason,reason_detail,customer_id,customer_name, carrier_id,carrier_name,deliver_id,deliver_name,warehouse_id,warehouse_name, bill_no,create_person_name,create_time, case ispay when '0' then '否' when '1' then '是' end as ispayStr, (case when reason_id=4 then pay_money else -pay_money end) pay_money, IF((ISNULL(is_delivery_free) || LENGTH(trim(is_delivery_free))<1),'0',is_delivery_free) as isDeliveryFree, delivery_cost as deliveryCost, case is_calculated when '0' then '未计算' when '1' then '已计算' end as is_calculated, case is_conflict when '0' then '否' when '1' then '是' end as isConflictStr,remark from fees_abnormal where 1=1 and is_calculated = '0' and reason_id = '2' and deliver_id in
#{item}
