and create_time >= #{operatorStartTime}
and create_time <= #{operatorEndTime}

select reason_id as reasonId, bill_no as billNo, reason, sum(pay_money+delivery_cost) as amount ,deliver_id as deliveryid,deliver_name as deliveryName from fees_abnormal where 1=1 and bill_no=#{billNo} and deliver_id in
#{item}
group by reason_id,bill_no,reason,deliver_id,deliver_name

    	select id,fee_no,reference,expressnum,order_no,reason,reason_detail,
    		carrier_name,deliver_name,customer_name,bill_no,create_person_name,create_time,
			case ispay
			 when '0' then '否'
			 when '1' then '是'
			end as ispayStr,
			(case when reason_id=4 then pay_money else -pay_money end) pay_money,
			case is_calculated
			  when '0' then '未计算'
			  when '1' then '已计算'
			end as is_calculated,
			case is_conflict
			when '0' then '否'
			 when    '1' then '是'
			end as isConflictStr,remark,warehouse_id,customer_id as customerId,ifnull(is_delivery_free,'0') as isDeliveryFree,
			-delivery_cost as deliveryCost
        from  fees_abnormal 
        where 1=1 and reason_id=#{reasonId} and bill_no=#{billNo}


        	and deliver_id=#{deliverid}



			and fee_no = #{feeNo}


			and expressnum = #{expressnum}

		select count(0) from fees_abnormal
        where 1=1 and is_calculated = '0'
        	and reason_id in('1','3','4','56')
        	and customer_id=#{customerid}

        	and create_time >= #{billstarttime}


        	and create_time <= #{billendtime}

		select sum(pay_money) from fees_abnormal
        where 1=1 and is_calculated = '0'
        	and reason_id in('1','3','4','56')
        	and customer_id=#{customerid}

        	and create_time >= #{billstarttime}


        	and create_time <= #{billendtime}

