mapper.select:
        select
        id, waybill_no, outstock_no, external_no, work_order_no, pay_id, duty_type, pay_type, customer_id, customer_name, warehouse_code, warehouse_name, carrier_id, carrier_name, deliver_id, deliver_name, pay_direction, is_delivery_free, amerce_amount, create_time, cre_person_id, cre_person, remark, mod_person_id, mod_person, mod_time, write_time, sync_time, bill_year, bill_month, waybill_time, del_flag,IF(is_delivery_free='0','否','是')
        , sum(product_amount) as product_amount, sum(returned_amount) as returned_amount        
        from fees_claims
        where 1 = 1 

				and waybill_no = #{waybillNo}


				and outstock_no = #{outstockNo}


				and external_no = #{externalNo}


				and work_order_no = #{workOrderNo}


				and customer_id = #{customerId}


				and customer_name = #{customerName}


				and warehouse_code = #{warehouseCode}


				and warehouse_name = #{warehouseName}


				and carrier_id = #{carrierId}


				and carrier_name = #{carrierName}


				and bill_year = #{billYear}


				and bill_month = #{billMonth}


				and create_time >= #{kesuStartTime}


				and create_time <= #{kesuEndTime}


				and waybill_time >= #{waybillStartTime}


				and waybill_time <= #{waybillEndTime}

		 group by waybill_no,work_order_no


	select warehouse_name,waybill_time,waybill_no,work_order_no,customer_name,duty_type,pay_type,product_amount,is_delivery_free,cre_person,remark from fees_claims
	where 1=1 and product_amount>0 

	   and bill_year=#{year}


	   and bill_month=#{month}


		AND customer_id = #{customerId}


  		and customer_id in

	 		#{item}


  	group by waybill_no,work_order_no

   select warehouse_name,waybill_time,waybill_no,work_order_no,customer_name,duty_type,pay_type,returned_amount,cre_person,remark from fees_claims
	where 1=1 and returned_amount>0

	   and bill_year=#{year}


	   and bill_month=#{month}


		AND customer_id = #{customerId}


  		AND customer_id in

	 		#{item}


	group by waybill_no,work_order_no
