mapper.select:
select t.oms_id,t.created_dt,t.order_no,t.outstock_no,t.temperature_type_code, t.routeNo,t.dispatcherName, t.transportNo,t.customer_name,t.customer_id,t.project_name, t.send_site,t.send_province,t.send_city,t.send_district, t.receive_site,t.receive_province,t.receive_city,t.receive_district, t.receive_address,t.actual_volume,t.actual_weight,t.light, t.actual_packing_qty,t.actual_goods_qty,t.receipt_packing_qty,t.receipt_goods_qty, t.has_backtrack,t.begin_time,t.end_time,t.del_flag, t.need_insurance,t.remark,t.car_model, sum(CASE b.subject_name WHEN '提货费' THEN b.amount ELSE 0 END) as tsTakes, sum(CASE b.subject_name WHEN '运费' THEN b.amount ELSE 0 END) as tsTransAmount, sum(CASE b.subject_name WHEN '送货费' THEN b.amount ELSE 0 END) as tsSend, sum(CASE b.subject_name WHEN '装货费' THEN b.amount ELSE 0 END) as ysZh, sum(CASE b.subject_name WHEN '卸货费' THEN b.amount ELSE 0 END) as ysXh, sum(CASE b.subject_name WHEN '逆向物流费' THEN b.amount ELSE 0 END) as tsReverseLogistic, sum(CASE b.subject_name WHEN '延时等待费' THEN b.amount ELSE 0 END) as tsDelayWaiting, sum(CASE b.subject_name WHEN '缠绕膜费' THEN b.amount ELSE 0 END) as tsWrappingFilm, sum(CASE b.subject_name WHEN '放空费' THEN b.amount ELSE 0 END) as tsEmptying, sum(CASE b.subject_name WHEN '赔付费' THEN b.amount ELSE 0 END) as tsClaim, sum(CASE b.subject_name WHEN '纸面回单费' THEN b.amount ELSE 0 END) as ysZmhd, sum(CASE b.subject_name WHEN '拆箱费' THEN b.amount ELSE 0 END) as ysCx, sum(CASE b.subject_name WHEN '贴码费' THEN b.amount ELSE 0 END) as ysTm, sum(CASE b.subject_name WHEN '保险费' THEN b.amount ELSE 0 END) as tsInsurance, sum(CASE b.subject_name WHEN '分流费' THEN b.amount ELSE 0 END) as tsFl, sum(CASE b.subject_name WHEN '上楼搬运费' THEN b.amount ELSE 0 END) as tsSlby, sum(CASE b.subject_name WHEN '过夜制冷费' THEN b.amount ELSE 0 END) as tsGyzl, sum(CASE b.subject_name WHEN '单据打印费' THEN b.amount ELSE 0 END) as tsDjdy, sum(CASE b.subject_name WHEN '加点费' THEN b.amount ELSE 0 END) as tsAddSite, sum(CASE b.subject_name WHEN '中转费' THEN b.amount ELSE 0 END) as tsZz, sum(CASE b.subject_name WHEN '垫付费' THEN b.amount ELSE 0 END) as tsDf, sum(CASE b.subject_name WHEN '押车费' THEN b.amount ELSE 0 END) as tsYc, sum(CASE b.subject_name WHEN '理货费' THEN b.amount ELSE 0 END) as tsTallying, sum(CASE b.subject_name WHEN '交货费' THEN b.amount ELSE 0 END) as tsJh, sum(CASE b.subject_name WHEN '过路费' THEN b.amount ELSE 0 END) as tsGl, sum(CASE b.subject_name WHEN '码货费' THEN b.amount ELSE 0 END) as tsMh, sum(CASE b.subject_name WHEN '托盘费' THEN b.amount ELSE 0 END) as tsPallet, SUM(b.amount_totle) as payTotalAmount from( select a.oms_id,a.created_dt,a.order_no,a.outstock_no,a.temperature_type_code, GROUP_CONCAT(c.route_no) as routeNo,GROUP_CONCAT(c.dispatcher_name) as dispatcherName, GROUP_CONCAT(c.transport_no) as transportNo,a.customer_name,a.customer_id,a.project_name, a.send_site,a.send_province,a.send_city,a.send_district, a.receive_site,a.receive_province,a.receive_city,a.receive_district, a.receive_address,a.actual_volume,a.actual_weight,a.light, a.actual_packing_qty,a.actual_goods_qty,a.receipt_packing_qty,a.receipt_goods_qty, a.has_backtrack,a.begin_time,a.end_time,a.del_flag, a.need_insurance,a.remark,c.car_model from fees_transport_master a LEFT JOIN fees_transport_routes c on a.oms_id=c.oms_id and c.del_flag='0' WHERE a.del_flag='0' GROUP BY a.order_no ) t LEFT JOIN fees_transport_items b on t.oms_id=b.oms_id and b.del_flag='0' where 1 = 1
and t.customer_id=#{customerId}
and t.project_name like CONCAT('%',#{projectName},'%')
and t.order_no=#{orderNo}
and t.transport_no=#{transportNo}
and t.del_flag=#{delFlag}
and t.begin_time>=#{beginTime}
and t.begin_time <= #{endTime}
GROUP BY t.order_no

select t.oms_id,t.created_dt,t.order_no,t.outstock_no,t.temperature_type_code, t.routeNo,t.dispatcherName, t.transportNo,t.customer_name,t.customer_id,t.project_name, t.send_site,t.send_province,t.send_city,t.send_district, t.receive_site,t.receive_province,t.receive_city,t.receive_district, t.receive_address,t.actual_volume,t.actual_weight,t.light, t.actual_packing_qty,t.actual_goods_qty,t.receipt_packing_qty,t.receipt_goods_qty, t.has_backtrack,t.begin_time,t.end_time,t.del_flag, t.need_insurance,t.remark,t.car_model, sum(CASE b.subject_name WHEN '提货费' THEN b.amount ELSE 0 END) as tsTakes, sum(CASE b.subject_name WHEN '运费' THEN b.amount ELSE 0 END) as tsTransAmount, sum(CASE b.subject_name WHEN '送货费' THEN b.amount ELSE 0 END) as tsSend, sum(CASE b.subject_name WHEN '装货费' THEN b.amount ELSE 0 END) as ysZh, sum(CASE b.subject_name WHEN '卸货费' THEN b.amount ELSE 0 END) as ysXh, sum(CASE b.subject_name WHEN '逆向物流费' THEN b.amount ELSE 0 END) as tsReverseLogistic, sum(CASE b.subject_name WHEN '延时等待费' THEN b.amount ELSE 0 END) as tsDelayWaiting, sum(CASE b.subject_name WHEN '缠绕膜费' THEN b.amount ELSE 0 END) as tsWrappingFilm, sum(CASE b.subject_name WHEN '放空费' THEN b.amount ELSE 0 END) as tsEmptying, sum(CASE b.subject_name WHEN '赔付费' THEN b.amount ELSE 0 END) as tsClaim, sum(CASE b.subject_name WHEN '纸面回单费' THEN b.amount ELSE 0 END) as ysZmhd, sum(CASE b.subject_name WHEN '拆箱费' THEN b.amount ELSE 0 END) as ysCx, sum(CASE b.subject_name WHEN '贴码费' THEN b.amount ELSE 0 END) as ysTm, sum(CASE b.subject_name WHEN '保险费' THEN b.amount ELSE 0 END) as tsInsurance, sum(CASE b.subject_name WHEN '分流费' THEN b.amount ELSE 0 END) as tsFl, sum(CASE b.subject_name WHEN '上楼搬运费' THEN b.amount ELSE 0 END) as tsSlby, sum(CASE b.subject_name WHEN '过夜制冷费' THEN b.amount ELSE 0 END) as tsGyzl, sum(CASE b.subject_name WHEN '单据打印费' THEN b.amount ELSE 0 END) as tsDjdy, sum(CASE b.subject_name WHEN '加点费' THEN b.amount ELSE 0 END) as tsAddSite, sum(CASE b.subject_name WHEN '中转费' THEN b.amount ELSE 0 END) as tsZz, sum(CASE b.subject_name WHEN '垫付费' THEN b.amount ELSE 0 END) as tsDf, sum(CASE b.subject_name WHEN '押车费' THEN b.amount ELSE 0 END) as tsYc, sum(CASE b.subject_name WHEN '理货费' THEN b.amount ELSE 0 END) as tsTallying, sum(CASE b.subject_name WHEN '交货费' THEN b.amount ELSE 0 END) as tsJh, sum(CASE b.subject_name WHEN '过路费' THEN b.amount ELSE 0 END) as tsGl, sum(CASE b.subject_name WHEN '码货费' THEN b.amount ELSE 0 END) as tsMh, sum(CASE b.subject_name WHEN '托盘费' THEN b.amount ELSE 0 END) as tsPallet, SUM(b.amount_totle) as payTotalAmount from( select a.oms_id,a.created_dt,a.order_no,a.outstock_no,a.temperature_type_code, GROUP_CONCAT(c.route_no) as routeNo,GROUP_CONCAT(c.dispatcher_name) as dispatcherName, GROUP_CONCAT(c.transport_no) as transportNo,a.customer_name,a.customer_id,a.project_name, a.send_site,a.send_province,a.send_city,a.send_district, a.receive_site,a.receive_province,a.receive_city,a.receive_district, a.receive_address,a.actual_volume,a.actual_weight,a.light, a.actual_packing_qty,a.actual_goods_qty,a.receipt_packing_qty,a.receipt_goods_qty, a.has_backtrack,a.begin_time,a.end_time,a.del_flag, a.need_insurance,a.remark,c.car_model from fees_transport_master a LEFT JOIN fees_transport_routes c on a.oms_id=c.oms_id and c.del_flag='0' GROUP BY a.order_no ) t LEFT JOIN fees_transport_items b on t.oms_id=b.oms_id and b.del_flag='0' where 1 = 1
and t.customer_id=#{customerId}
and t.project_name like CONCAT('%',#{projectName},'%')
and t.order_no=#{orderNo}
and t.transport_no=#{transportNo}
and t.del_flag=#{delFlag}
and t.begin_time>=#{beginTime}
and t.begin_time < #{endTime}
GROUP BY t.order_no ORDER BY t.begin_time,t.created_dt,t.outstock_no ASC
