mapper.update:
update bms_correct_asyn_task
task_id=#{taskId,jdbcType=VARCHAR}, create_month=#{createMonth,jdbcType=VARCHAR}, customerId=#{customerId,jdbcType=VARCHAR}, task_name=#{taskName,jdbcType=VARCHAR}, task_rate=#{taskRate,jdbcType=INTEGER}, task_status=#{taskStatus,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, finish_time=#{finishTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR} last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP},
where id=#{id}

mapper.select:
select count(*) from bms_correct_asyn_task where del_flag='0' and
customerId=#{customerId} and startDate=#{startDate} and endDate=#{endDate}
and task_status in ('WAIT','PROCESS')

    	select DISTINCT customerid from biz_dispatch_bill where del_flag=0 
			and create_time>=#{startDate} and create_time<#{endDate} 
			and customerid is not null
