and del_flag = '0'

mapper.select:
		select

        from price_contract_info 
        where 1=1
        and start_date < current_timestamp()  and expire_date >= current_timestamp()

			and contract_type_code = #{contractTypeCode}


			and customerId = #{customerId}


			and contract_state = #{contractState}


			and customerId in 

					#{item.deliverid}


        and del_flag = 0 and contract_state='1'

mapper.update:
update price_contract_info
contract_type_code=#{contractTypeCode,jdbcType=VARCHAR}, customerId=#{customerId,jdbcType=VARCHAR}, customerName=#{customerName,jdbcType=VARCHAR}, contract_obj=#{contractObj,jdbcType=VARCHAR}, contract_state=#{contractState,jdbcType=VARCHAR}, paper_contract_no=#{paperContractNo,jdbcType=VARCHAR}, start_date=#{startDate,jdbcType=TIMESTAMP}, expire_date=#{expireDate,jdbcType=TIMESTAMP}, description=#{description,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
        select
         id, quotation_no, biz_type_code, subject_id, fee_unit_code, unit_price, description, remark, creator, create_time, last_modifier, last_modify_time, del_flag
        from price_general_quotation
        where del_flag = 0 

				and quotation_no = #{quotationNo}


				and biz_type_code = #{bizTypeCode}


				and subject_id = #{subjectId}


				and fee_unit_code = #{feeUnitCode}


			    and create_time >= #{startTime} and create_time <= #{endTime}

        select
        		item.id, item.contract_code, item.subject_id, item.template_id, item.creator, item.create_time, item.last_modifier, item.last_modify_time, item.del_flag
        from price_contract_item item
        LEFT JOIN system_code b on item.subject_id = b.`code`
        LEFT JOIN price_contract_info pci on item.contract_code = pci.contract_code and pci.del_flag='0' and pci.contract_state='1'	
        LEFT JOIN price_transport_template ptt on item.subject_id = ptt.template_code and ptt.del_flag='0'

			and ptt.template_type_code = #{templateTypeCode}

        where item.del_flag='0' and b.status='0'

			and item.contract_code = #{contractCode}


			and item.subject_id = #{subjectId}

        select
        		item.id, item.contract_code, item.subject_id, item.template_id, item.creator, item.create_time, item.last_modifier, item.last_modify_time, item.del_flag
        from price_contract_item item
        LEFT JOIN system_code b on item.subject_id = b.`code`
        LEFT JOIN price_contract_info pci on item.contract_code = pci.contract_code and pci.del_flag='0' and pci.contract_state='1'	
        LEFT JOIN price_out_transport_template ptt on item.subject_id = ptt.template_code and ptt.del_flag='0'

			and ptt.template_type_code = #{templateTypeCode}

        where item.del_flag='0' and b.status='0'

			and item.contract_code = #{contractCode}


			and item.subject_id = #{subjectId}

mapper.update:
update price_contract_item
subject_id=#{subjectId,jdbcType=VARCHAR}, template_id=#{templateId,jdbcType=VARCHAR}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}
where
contract_code=#{contractCode}

mapper.select:
SELECT e.template_type_code transportCode,e.template_name transportName,e.remark transportRemark, d.storage_template_type storageCode,d.template_name storageName,d.remark storageRemark, f.template_name dispatchName,f.remark dispatchRemark, a.template_id,a.subject_id subjectId, c.remark generalStorageRemark, IFNULL(c.rule_no,d.quotation_no) as ruleNo, IFNULL(g.instruction,h.instruction) as ruleInstruction from price_contract_item a LEFT JOIN price_general_quotation c ON a.template_id=c.quotation_no and c.del_flag=0 LEFT JOIN price_general_template d ON a.template_id=d.template_code and d.del_flag=0 LEFT JOIN price_transport_template e on a.template_id=e.template_code and e.del_flag=0 LEFT JOIN price_dispatch_template f ON a.template_id=f.template_code and f.del_flag=0 LEFT JOIN bill_rule_receive g on g.quotationNo=c.rule_no and g.subject_id=c.subject_id and g.del_flag=0 LEFT JOIN bill_rule_receive h on h.quotationNo=d.quotation_no and h.subject_id=d.subject_id and h.del_flag=0 where 1=1
and a.contract_code=#{contractCode}
and a.subject_id=#{subjectId}
and a.biz_type_code=#{bizTypeCode}
and a.del_flag='0'

   		select subject_id subjectId,template_code templateId
		from price_contract_discount_item
		where 1=1 

		   and contract_code=#{contractCode}  


			and subject_id=#{subjectId}


			and biz_type_code=#{bizTypeCode}

			and del_flag='0'

SELECT e.template_type_code transportCode,e.template_name transportName, e.remark transportRemark,d.storage_template_type storageCode, d.template_name storageName,c.remark generalStorageRemark, d.remark storageRemark,f.template_name dispatchName,f.remark dispatchRemark, a.template_id,a.subject_id subjectId,b.code_name codeName,b.type_code codeType, b.code code,i.instruction as ruleInstruction,h.rule_no as ruleNo from price_contract_item a LEFT JOIN system_code b on a.subject_id=b.`code` LEFT JOIN price_general_quotation c ON a.template_id=c.quotation_no and c.del_flag=0 LEFT JOIN price_general_template d ON a.template_id=d.template_code and d.del_flag=0 left JOIN price_transport_template e on a.template_id=e.template_code and e.del_flag=0 LEFT JOIN price_dispatch_template f ON a.template_id=f.template_code and f.del_flag=0 left join price_contract_info g on g.contract_code=a.contract_code and g.del_flag=0 left join pub_rule_deliver_payable h on h.deliveryid=g.customerId and h.subject_id=a.subject_id and h.del_flag=0 left join bill_rule_pay i on i.quotationNo=h.rule_no and i.del_flag=0 where 1=1
and a.contract_code=#{contractCode}
and a.subject_id=#{subjectId}
and a.biz_type_code=#{bizTypeCode}
and a.del_flag='0' and b.status='0'

select item.id, item.contract_code, item.subject_id, item.template_id, item.creator, item.create_time, item.last_modifier, item.last_modify_time, item.del_flag from price_contract_item item LEFT JOIN system_code b on item.subject_id = b.`code` LEFT JOIN price_contract_info pci on item.contract_code = pci.contract_code and item.del_flag='0' and pci.del_flag='0' where 1=1
and item.contract_code = #{contractCode}
and item.subject_id = #{subjectId}
and item.del_flag='0' and pci.contract_state='1' and b.status='0'

SELECT e.template_type_code transportCode,e.template_name transportName, e.remark transportRemark,d.storage_template_type storageCode, d.template_name storageName,c.remark generalStorageRemark, d.remark storageRemark,f.template_name dispatchName, f.remark dispatchRemark,g.remark dispatchOtherRemark, a.template_id,a.subject_id subjectId, j.instruction as ruleInstruction,i.rule_no as ruleNo from price_contract_item a LEFT JOIN price_general_quotation c ON a.template_id=c.quotation_no and c.del_flag=0 LEFT JOIN price_general_template d ON a.template_id=d.template_code and d.del_flag=0 LEFT JOIN price_out_transport_template e on a.template_id=e.template_code and e.del_flag=0 LEFT JOIN price_out_dispatch_template f ON a.template_id=f.template_code and f.del_flag=0 LEFT JOIN price_out_dispatch_other_template g on a.template_id=g.template_code and g.del_flag=0 left join price_contract_info h on h.contract_code=a.contract_code and h.del_flag=0 left join pub_rule_deliver_payable i on i.deliveryid=h.customerId and i.subject_id=a.subject_id and i.del_flag=0 left join bill_rule_pay j on j.quotationNo=i.rule_no and j.del_flag=0 where 1=1
and a.contract_code=#{contractCode}
and a.subject_id=#{subjectId}
and a.del_flag='0'
