mapper.select:
        select
        d.*, s.servicename as servicename
        from bms_quote_dispatch_detail d
		LEFT JOIN price_dispatch_template t on d.template_code=t.template_code
		LEFT JOIN pub_carrier_servicetype s on s.servicecode=d.service_type_code and s.carrierid = #{carrierid} and s.delflag='0'
        where 1=1 and d.del_flag = '0'

				and d.id = #{id}


				and d.template_code = #{templateCode}


				and d.start_warehouse_id = #{startWarehouseId}


				and d.province_id = #{provinceId}


				and d.city_id = #{cityId}


				and d.area_id = #{areaId}


				and d.weight_down = #{weightDown}


				and d.weight_up = #{weightUp}


				and d.unit_price = #{unitPrice}


				and d.first_weight = #{firstWeight}


				and d.first_weight_price = #{firstWeightPrice}


				and d.continued_weight = #{continuedWeight}


				and d.continued_price = #{continuedPrice}


				and d.weight_limit = #{weightLimit}


				and d.timeliness = #{timeliness}


				and d.temperature_type_code = #{temperatureTypeCode}


				and d.product_case = #{productCase}


				and d.area_type_code = #{areaTypeCode}


				and d.deliverid = #{deliverid}


				and d.mark = #{mark}


				and d.creator = #{creator}


			    and d.create_time >= #{createTime} add d.create_time <= #{createTime}


				and d.last_modifier = #{lastModifier}


			    and d.last_modify_time >= #{lastModifyTime} add d.last_modify_time <= #{lastModifyTime}


				and d.del_flag = #{delFlag}


				and d.service_type_code = #{serviceTypeCode}

		ORDER BY d.create_time desc,d.start_warehouse_id,d.province_id

SELECT
d.*, s.servicename as servicename
FROM bms_quote_dispatch_detail d
LEFT JOIN pub_carrier_servicetype s on s.servicecode=d.service_type_code and s.delflag='0'and s.carrierid=#{carrierid} 
where 1=1 AND d.del_flag = '0' AND d.template_code=#{templateCode,jdbcType=VARCHAR}

mapper.update:
update bms_quote_dispatch_detail
template_code=#{templateCode,jdbcType=VARCHAR}, start_warehouse_id=#{startWarehouseId,jdbcType=VARCHAR}, province_id=#{provinceId,jdbcType=VARCHAR}, city_id=#{cityId,jdbcType=VARCHAR}, area_id=#{areaId,jdbcType=VARCHAR}, weight_down=#{weightDown,jdbcType=DECIMAL}, weight_up=#{weightUp,jdbcType=DECIMAL}, unit_price=#{unitPrice,jdbcType=DECIMAL}, first_weight=#{firstWeight,jdbcType=DECIMAL}, first_weight_price=#{firstWeightPrice,jdbcType=DECIMAL}, continued_weight=#{continuedWeight,jdbcType=DECIMAL}, continued_price=#{continuedPrice,jdbcType=DECIMAL}, weight_limit=#{weightLimit,jdbcType=DECIMAL}, timeliness=#{timeliness,jdbcType=VARCHAR}, temperature_type_code=#{temperatureTypeCode,jdbcType=VARCHAR}, product_case=#{productCase,jdbcType=VARCHAR}, area_type_code=#{areaTypeCode,jdbcType=VARCHAR}, deliverid=#{deliverid,jdbcType=VARCHAR}, mark=#{mark,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, service_type_code=#{serviceTypeCode,jdbcType=VARCHAR},
where id=#{id}

mapper.delete:
update bms_quote_dispatch_detail
last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag = '1'
where id = #{id, jdbcType=BIGINT}
