mapper.sql:

		and id = #{id}


		and template_code like '%${templateCode}%'


		and template_name like '%${templateName}%'


		and template_type = #{templateType}


		and remark like '%${remark}%'


		and param1 = #{param1}


		and creator = #{creator}


	    and create_time >= #{createTime} add create_time <= #{createTime}


		and last_modifier = #{lastModifier}


	    and last_modify_time >= #{lastModifyTime} add last_modify_time <= #{lastModifyTime}


		and deliverId = #{deliverId}


		and deliverName = #{deliverName}

and del_flag = '0'

mapper.update:
update price_out_dispatch_other_template
template_code=#{templateCode,jdbcType=VARCHAR}, template_name=#{templateName,jdbcType=VARCHAR}, template_type=#{templateType,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, param1=#{param1,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, deliverId=#{deliverId,jdbcType=VARCHAR}, deliverName=#{deliverName,jdbcType=VARCHAR}
where id=#{id}

	update price_out_dispatch_other_template set del_flag='1'
	,last_modifier=#{lastModifier,jdbcType=VARCHAR}
	,last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}
    where id=#{id}
