mapper.select:
        select p.*, r.quotationName as ruleName, g.subject_name as subjectName from price_general_quotation p
		LEFT JOIN bill_rule_receive r on p.rule_no=r.quotationNo and p.subject_id=r.subject_id and r.del_flag='0'
		LEFT JOIN bms_subject_info g on g.subject_code=p.subject_id and g.in_out_typecode='INPUT' and g.del_flag='0'
        where p.del_flag = 0 

			and p.quotation_no like '%${quotationNo}%' 


			and p.remark like '%${remark}%' 


			and p.biz_type_code = #{bizTypeCode}


			and p.price_type = #{priceType}


			and p.template_type = #{templateType}


			and p.subject_id = #{subjectId}


			and p.fee_unit_code = #{feeUnitCode}


		    and p.create_time >= #{startTime} and p.create_time <= #{endTime}


			and p.customerId=#{customerId}

		order by p.create_time  desc

select

   from price_general_quotation
   where del_flag = 0 and quotation_no=#{quotationNo}

        select

        from price_general_quotation
        where del_flag = 0 

			and quotation_no like '%${quotationNo}%' 


			and remark like '%${remark}%' 


			and biz_type_code = #{bizTypeCode}


			and price_type = #{priceType}


			and template_type = #{templateType}


			and subject_id = #{subjectId}


			and fee_unit_code = #{feeUnitCode}


		    and create_time >= #{startTime} and create_time <= #{endTime}


			and customerId = #{customerId}

		order by create_time  desc
		limit 1

mapper.update:
update price_general_quotation
quotation_no=#{quotationNo,jdbcType=VARCHAR}, customerId=#{customerId}, customerName=#{customerName}, biz_type_code=#{bizTypeCode,jdbcType=VARCHAR}, template_type=#{templateType,jdbcType=VARCHAR}, subject_id=#{subjectId,jdbcType=VARCHAR}, fee_unit_code=#{feeUnitCode,jdbcType=VARCHAR}, unit_price=#{unitPrice,jdbcType=DOUBLE}, description=#{description,jdbcType=VARCHAR}, price_type=#{priceType,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, param1=#{param1,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, rule_no=#{ruleNo,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
select b.* from price_contract_item a
inner join price_general_quotation b on a.template_id=b.quotation_no and b.del_flag=0
 where a.contract_code=#{contractCode}
and a.subject_id=#{subjectId} and a.del_flag=0;

SELECT pg.* from  price_general_quotation pg
where pg.template_type = 'S' and pg.del_flag = 0

	and pg.subject_id = #{subjectId}
