mapper.select:
        select

        from price_storage_step
        where del_flag = 0 

				and quotation_id = #{quotationId}



			    and create_time >= #{startTime} add create_time <= #{endTime}

		order by id

mapper.update:
update price_storage_step
warehouse_code=#{warehouseCode,jdbcType=VARCHAR}, quotation_id=#{quotationId,jdbcType=VARCHAR}, startDate=#{startDate,jdbcType=TIMESTAMP}, expireDate=#{expireDate,jdbcType=TIMESTAMP}, weight_lower=#{weightLower,jdbcType=DECIMAL}, weight_upper=#{weightUpper,jdbcType=DECIMAL}, temperature_type_code=#{temperatureTypeCode,jdbcType=VARCHAR}, unit_price=#{unitPrice,jdbcType=DECIMAL}, continued_item=#{continuedItem,jdbcType=DECIMAL}, remark=#{remark,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, num_upper=#{numUpper,jdbcType=DECIMAL}, num_lower=#{numLower,jdbcType=DECIMAL}, sku_upper=#{skuUpper,jdbcType=DECIMAL}, sku_lower=#{skuLower,jdbcType=DECIMAL}, volume_upper=#{volumeUpper,jdbcType=DECIMAL}, volume_lower=#{volumeLower,jdbcType=DECIMAL}, user_define1=#{userDefine1,jdbcType=VARCHAR}, user_define2=#{userDefine2,jdbcType=VARCHAR}, user_define3=#{userDefine3,jdbcType=VARCHAR}, user_define4=#{userDefine4,jdbcType=VARCHAR}, user_define5=#{userDefine5,jdbcType=VARCHAR}, first_num=#{firstNum,jdbcType=DECIMAL}, first_price=#{firstPrice,jdbcType=DECIMAL}, continued_price=#{continuedPrice,jdbcType=DECIMAL},
where id=#{id}
