mapper.select:
        select
        p.*, r.quotationName as quotationName
        from price_general_template p
        LEFT JOIN bill_rule_receive r on p.quotation_no=r.quotationNo
        where 1=1 and p.del_flag=0

				and p.customerId = #{customerId}


				and p.template_code  like '%${templateCode}%' 


				and p.template_name  like  '%${templateName}%'


				and p.template_type = #{templateType}


				and p.remark  like  '%${remark}%'


				and p.storage_template_type = #{storageTemplateType}


			    and p.create_time >= #{startTime} and p.create_time <= #{endTime}

		order by p.create_time desc,p.id desc

        select

        from price_general_template
        where 1=1 and del_flag='0'

				and template_code  like '%${templateCode}%' 


				and template_name  like  '%${templateName}%'


				and template_type = #{templateType}


				and remark  like  '%${remark}%'


				and storage_template_type = #{storageTemplateType}


			    and create_time >= #{startTime} and create_time <= #{endTime}


				and customerId = #{customerId}

		order by create_time desc,id desc
		limit 1

mapper.update:
update price_general_template
customerId=#{customerId,jdbcType=VARCHAR}, customerName=#{customerName,jdbcType=VARCHAR}, template_name=#{templateName,jdbcType=VARCHAR}, biz_type_code=#{bizTypeCode,jdbcType=VARCHAR}, template_type=#{templateType,jdbcType=VARCHAR}, subject_id=#{subjectId,jdbcType=VARCHAR}, bill_way_code=#{billWayCode,jdbcType=VARCHAR}, storage_template_type=#{storageTemplateType,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, param1=#{param1,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, quotation_no=#{quotationNo,jdbcType=VARCHAR},
where id=#{id}

update price_general_template set del_flag=1,
last_modifier=#{lastModifier},last_modify_time=#{lastModifyTime}
where id=#{templateId}
