mapper.select:
	select a.seller_name as sellerName, sum(un_receipt_amount) as unReceiptAmount from bill_check_info 
	where 1=1

		and seller_id in

             	#{item}


	and create_month < #{createMonth}
	group by a.seller_id

	select a.seller_name as sellerName, sum(b.receipt_amount) as receiptAmount from bill_check_info  a
	inner join bill_check_receipt b on a.id = b.bill_check_id
	where 1=1

		and seller_id in

             	#{item}


	and a.create_month < #{createMonth}
	and b.receipt_date >= #{receiptDate}
	group by a.seller_id

        select a.group_name as groupName,a.seller_id as sellerId,a.seller_name as sellerName,a.un_receipt_amount as unReceiptAmount,b.receipt_amount as receiptAmount,sum(IFNULL(un_receipt_amount,0)+IFNULL(receipt_amount,0)) as totalAmount from
		(select g.group_name,i.seller_id,i.seller_name,sum(un_receipt_amount) as un_receipt_amount from bill_check_info i 
		LEFT JOIN bms_group_user u on i.seller_id=u.user_id
		LEFT JOIN bms_group g on g.id=u.group_id
		where 1=1

			and i.seller_name like CONCAT('%',#{sellerName},'%')


			and i.seller_id in

	        	#{item}



			and g.group_code=#{groupCode}

		and create_month < #{createMonth}
		GROUP BY i.seller_id
		) a left JOIN
		(
		select g.group_name,a.seller_name AS seller_name,sum(b.receipt_amount) as receipt_amount from bill_check_info  a
		inner join bill_check_receipt b on a.id = b.bill_check_id
		LEFT JOIN bms_group_user u on a.seller_id=u.user_id
		LEFT JOIN bms_group g on g.id=u.group_id
		where 1=1 

			and a.seller_name like CONCAT('%',#{sellerName},'%')


			and a.seller_id in

	             	#{item}



			and g.group_code=#{groupCode}

		and a.create_month < #{createMonth}
		and b.receipt_date>=#{receiptDate}
		GROUP BY a.seller_id
		) b on a.seller_name=b.seller_name
		GROUP BY a.seller_id
