mapper.sql:

		and a.id = #{id}


		and a.quotationNo = #{quotationNo}


		and a.quotationName = #{quotationName}


		and a.biz_type_code = #{bizTypeCode}


		and a.subject_id = #{subjectId}


		and a.remark = #{remark}


		and a.creator = #{creator}


	    and a.create_time >= #{startTime}


	    and a.create_time <= #{endTime} 



		and a.last_modifier = #{lastModifier}


	    and a.last_modify_time >= #{lastModifyTime} add a.last_modify_time <= #{lastModifyTime}


		and a.rule = #{rule}


		and quo_modus = #{quoModus}


	    and a.is_default=#{isDefault}

and a.del_flag = '0' and a.visible='0'

mapper.update:
update bill_rule_receive
quotationNo=#{quotationNo,jdbcType=VARCHAR}, quotationName=#{quotationName,jdbcType=VARCHAR}, biz_type_code=#{bizTypeCode,jdbcType=VARCHAR}, subject_id=#{subjectId,jdbcType=VARCHAR}, remark=#{remark,jdbcType=VARCHAR}, creator=#{creator,jdbcType=VARCHAR}, create_time=#{createTime,jdbcType=TIMESTAMP}, last_modifier=#{lastModifier,jdbcType=VARCHAR}, last_modify_time=#{lastModifyTime,jdbcType=TIMESTAMP}, del_flag=#{delFlag,jdbcType=VARCHAR}, rule=#{rule,jdbcType=VARCHAR}, instruction=#{instruction,jdbcType=VARCHAR}, quo_modus=#{quoModus,jdbcType=VARCHAR}, sort_no=#{sortNo,jdbcType=INTEGER}, visible=#{visible,jdbcType=VARCHAR}, is_default=#{isDefault,jdbcType=VARCHAR},
where id=#{id}

mapper.select:
select b.rule, a.quotation_no from price_general_template a
left join bill_rule_receive b on a.quotation_no = b.quotationNo
where a.customerid = #{customerid}  and a.subject_id = #{subjectId} and a.del_flag = 0 and b.del_flag = 0 limit 1;

select id, quotationNo, quotationName, biz_type_code, remark, creator, create_time, last_modifier, last_modify_time, del_flag, rule ,instruction		
from bill_rule_receive
where 1=1 and quo_modus=#{quoModus}

	and biz_type_code=#{bizTypeCode}

and del_flag = '0'
