var date=new Date;  
var year=date.getFullYear();  
var month=date.getMonth();
var createMonth = "";
if(month < 10){
	createMonth = year.toString().substring(2,4) + "0" + month;
}else{
	createMonth = year.toString().substring(2,4) + month;
}

view.get("#queryForm.entity").set("createMonthStart",createMonth);
view.get("#queryForm.entity").set("createMonthEnd",createMonth);

