FloatPanel.ClientEvent:
var data = this.get("#viewSelectInvoice.userData");
if (data != null) {
	view.get("#AutoForma.entity").set("invoiceName", data.get("customName")), view.get("#AutoForma.entity").set("invoiceId", data.get("customNo")), view.get("#AutoForma.entity").set("billName", data.get("customName"))
	
	/*
	 var datas = view.get("#dsBillReceiveMasterEntityQuery.data").current;
	 datas.set({
	 invoiceName:data.get("customName")
	 });
	 */
	view.id("getInportParamAction").set("parameter", {
		invoiceName: data.get("customName")
	}).execute(function(result){
		view.get("#AutoForma.entity").set("billStartTime", result["billStartTime"]);
		view.get("#AutoForma.entity").set("firstClassName", result["firstClassName"]);
		view.get("#AutoForma.entity").set("bizTypeName", result["bizTypeName"]);
		view.get("#AutoForma.entity").set("projectName", result["projectName"]);
		view.get("#AutoForma.entity").set("sellerId", result["sellerId"]);
		view.get("#AutoForma.entity").set("sellerName", result["sellerName"]);
		view.get("#AutoForma.entity").set("deptName", result["deptName"]);
		view.get("#AutoForma.entity").set("deptCode", result["deptCode"]);
		view.get("#AutoForma.entity").set("projectManagerId", result["projectManagerId"]);
		view.get("#AutoForma.entity").set("projectManagerName", result["projectManagerName"]);
		view.get("#AutoForma.entity").set("balanceId", result["balanceId"]);
		view.get("#AutoForma.entity").set("balanceName", result["balanceName"]);
		view.get("#AutoForma.entity").set("confirmManId", result["confirmManId"]);
		view.get("#AutoForma.entity").set("confirmMan", result["confirmMan"]);
		view.get("#AutoForma.entity").set("confirmDate", result["confirmDate"]);
		if (result["isneedInvoice"] == '1') {
			result["isneedInvoice"] = '是';
		}
		if (result["isneedInvoice"] == '0') {
			result["isneedInvoice"] = '否';
		}
		view.get("#AutoForma.entity").set("isneedInvoice", result["isneedInvoice"]);
	});
}
