Button.ClientEvent:
var data=view.get("#dsBmsBillInfo.data").current;
var billname = data.get("billName");
var customerid = data.get("customerId");
var billstarttime = data.get("startTime");
var billendtime = data.get("endTime");
var seperator = "-";
if(isNullOrEmpty(billname)){
	dorado.MessageBox.alert("账单名称不能为空");
	return;
}
if(isNullOrEmpty(customerid)){
	dorado.MessageBox.alert("请选择商家");
	return;
}
if(isNullOrEmpty(billendtime)){
	dorado.MessageBox.alert("结束日期不能为空");
	return;
}
if(!isNullOrEmpty(billstarttime)){
	if(billstarttime.getTime()>billendtime.getTime()){
		dorado.MessageBox.alert("开始时间不能大于结束时间！");
		return;
	}
}	


//如果商家未选择开始时间，默认往前推两个月，使其取到上上月的遗漏数据
if(isNullOrEmpty(billstarttime)){
	//上月
	var date=new Date();
	var year = date.getFullYear();
	var month = formatMonth(date.getMonth()-1);
	var firstDay = year + seperator + month + seperator + "01";//上个月的第一天
	if(new Date(firstDay).getTime() > billendtime.getTime()){
		dorado.MessageBox.alert("默认开始时间为("+ firstDay +")，不能大于结束时间！");
		return;
	}
	data.set("startTime",firstDay);
}

//判断是否超过两个小时
var m=getDays(data.get("startTime"),data.get("endTime"));
if(m>62){
	dorado.MessageBox.alert("账单开始时间和结束时间间隔不能超过两个月!");
	return;
}

function getDays(strDateStart,strDateEnd){
  var iDays = parseInt(Math.abs(strDateEnd - strDateStart ) / 1000 / 60 / 60 /24)//把相差的毫秒数转换为天数
   return iDays ;
}


