var data = this.get("#viewSelectCustomer2.userData");
var customerid="";
if (data != null) {
	var entity = this.get("#dsBmsBillInfo").getData("#");
	customerid= data.get("customerid");
	var endTime=entity.get("endTime").toISOString().slice(2,7);
    entity.set({
        customerId: customerid,
		customerName: data.get("customername"),
		billName:"九曳-"+data.get("customername")+"-"+endTime+"账单"
    });
	
	var ajaxaction=view.get("#ajaxgettimeAction");
	ajaxaction.set("parameter",customerid).execute(function(result){
		if(result==null||result==""){
			view.id("infoContainer").set("content","商家【"+ customerid+"】还未生成账单");
		}else{
			var billendtime = result.endTime.replace("T"," ").replace("Z","");
			view.id("infoContainer").set("content","商家账单【"+result.billNo+"】最后生成时间<span style='color:red;'>"+billendtime+"</span>");
		}
	});
}

