AutoFormElement.ClientEvent:
var data=view.get("#dsBmsBillInfo.data").current;
var discountRate=data.get("discountRate");
if(discountRate!=null&&discountRate!=""){
	var totalAmount=data.get("totalAmount");
	var derateAmount=data.get("derateAmount");
	var subjectDiscountAmount=data.get("subjectDiscountAmount");
	var discountAmount=(totalAmount*discountRate/100).toFixed(2);
	var receiptAmount=(totalAmount*100-discountAmount*100-derateAmount*100-subjectDiscountAmount*100)/100;
	data.set("receiveAmount",receiptAmount);
	data.set("discountAmount",discountAmount);
}

