		{
		    tagName: "A",
		    href: "#",
		    content:"收款",
			style:"margin-right:10px;",
		    onclick: function() {
				//获取条件查询数据
				var queryJson={
					id:arg.data.get("id"),
					billNo:arg.data.get("billNo")
				};
				with (view.get("#dsBillReceiptInfo")){
					set("parameter", queryJson);
					flush();
				}
				view.get("#receiptDialog").show();
	    	}
		},
		{
		    tagName: "A",
		    href: "#",
		    content:"删除",
			style:"margin-right:10px;",
		    onclick: function() {
				var invoceNo="";
				if(!isNullOrEmpty(arg.data.get("invoceNo"))){
					invoceNo=arg.data.get("invoceNo");
				}
				dorado.MessageBox.confirm("确定要删除发票" 
					+ invoceNo+"？\n发票金额为：" + arg.data.get("invoceAmount"), function() {
					var action=view.get("#delInvoceAction");
					action.execute(function(result){
				 		alertMsg(result.code, result.message);
						//获取条件查询数据
						with (view.get("#dsBillInvoceInfoList")){
							//获取条件查询数据&#xD;
							var queryJson={
								billNo:arg.data.get("billNo")
							};
							set("parameter", queryJson);
							flush();
						}
				 	});
				}); 
	    	}
		}
	]);
}else if(status == 'SETTLED'){
	//已确认(详细、开票、删除、导出)
	jQuery(arg.dom).xCreate([
		{
		    tagName: "A",
		    href: "#",
		    content:"删除",
			style:"margin-right:10px;",
		    onclick: function() {
				var invoceNo="";
				if(!isNullOrEmpty(arg.data.get("invoceNo"))){
					invoceNo=arg.data.get("invoceNo");
				}
				dorado.MessageBox.confirm("确定要删除发票" 
					+ invoceNo+"？\n发票金额为：" + arg.data.get("invoceAmount")+"元", function() {
					var action=view.get("#delInvoceAction");
