	action.set("parameter",data).execute(function(){
		dorado.MessageBox.alert("删除成功!");
		with (view.get("#dsFileExportTaskList")){
			//定义查询条件json结构
			var queryJson={
				taskType:'recbill'
			};
			set("parameter", queryJson);
			flushAsync();
		}
	});
})

//定义一个函数，判断参数是否为空
function isNullOrEmpty(e) {
    if (e == null || e == undefined || e == "") {
        return true;
    } else {
        return false;
    }
}

