View.ClientEvent:
var myframe= window.parent.$id("detailFrame").objects[0];
if(!isNullOrEmpty(myframe)){
	var userData=myframe.get("userData");
	if(userData){
		var billno=userData;
		var ds=view.get("#dsWarehouseGroup");
		ds.set("parameter",billno).flush();//不能用异步刷新
		var datas=view.get("#dsWarehouseGroup.data");
		var amount=0;
		datas.each(function(item){
			amount=addNum(amount,item.get("amount"));
		});
		var tab=view.get("#storagedispatchTab");
		tab.set("caption","仓储配送( "+amount+" )");
		//异常费用 
	}else{
		dorado.MessageBox.alert("账单编号为空");
	}

}

Button.ClientEvent:
var data=view.get("#dsQuery.data").current;
var billno=data.get("billNo");
if(billno){
	var ds=view.get("#dsWarehouseGroup");
	ds.set("parameter",billno).flush();//不能用异步刷新
	var datas=view.get("#dsWarehouseGroup.data");
	var amount=0;
	datas.each(function(item){
		amount=addNum(amount,item.get("amount"));
	});
	var tab=view.get("#storagedispatchTab");
	tab.set("caption","仓储配送( "+amount+" )");
	//异常费用 
	var dsAbnormal=view.get("#dsAbnormalWareHouseBill");
	dsAbnormal.set("parameter",billno).flush();//不能用异步刷新
	var abnormalAmount=0;
	var abnormalData=view.get("#dsAbnormalWareHouseBill.data");
	abnormalData.each(function(item){
		abnormalAmount=addNum(abnormalAmount,item.get("amount"));
	});
	var abnormalTab=view.get("#abnormalTab");
	abnormalTab.set("caption","理赔( "+abnormalAmount+" )");
}else{
	dorado.MessageBox.alert("请输入账单编号");
}

DataGrid.ClientEvent:
var data=view.get("#dsWarehouseGroup.data").current;
if(data){
	var parameter={
		billNo:data.get("billNo"),
		warehouseCode:data.get("warehouseCode"),
