		billNo:data.get("billNo"),
		warehouseCode:data.get("warehouseCode"),
		feesType:"DISPATCH"
	}
	var ds=view.get("#dsDispathBillSubjectInfo");
	ds.set("parameter",parameter).flush();
	
	var storageTab=view.get("#storageTab");
	var storagedatas=view.get("#dsBillSubjectInfo.data");
	var amount=0;
	storagedatas.each(function(item){
		amount=addNum(amount,item.get("totalAmount"));
	});
	storageTab.set("caption","仓储( "+amount+" )");
	var dispatchTab=view.get("#dispatchTab");
	var dispatchDatas=view.get("#dsDispathBillSubjectInfo.data");
	var amountd=0;
	dispatchDatas.each(function(item){
		amountd=addNum(amountd,item.get("totalAmount"));
	});
	dispatchTab.set("caption","配送( "+amountd+" )");
}



function addNum (num1, num2) {
	 var sq1,sq2,m;
	 try {
	  sq1 = num1.toString().split(".")[1].length;
	 }
	 catch (e) {
	  sq1 = 0;
	 }
	 try {
	  sq2 = num2.toString().split(".")[1].length;
	 }
	 catch (e) {
	  sq2 = 0;
	 }
	 m = Math.pow(10,Math.max(sq1, sq2));
	 return (num1 * m + num2 * m) / m;
}

var data=view.get("#dsBillSubjectInfo.data").current;
var subjectCode=data.get("subjectCode");
view.get("#lblBillNo").set("text",data.get("billNo"));
var tab={};
tab.$type = "IFrame";
tab.closeable = true;
switch(subjectCode){
	case "wh_product_storage":
	case "wh_product_pallet_storage":
	tab.name="商品存储费";
	tab.caption="商品存储费";
	tab.path="com.jiuyescm.bms.bill.receive.lookup.ProductStorageLookUp.d";
	break;
	case "wh_material_storage":
	tab.name="耗材存储费";
	tab.caption="耗材存储费";
	tab.path="com.jiuyescm.bms.bill.receive.lookup.PackStorageLookUp.d";
	break;
	case "wh_b2c_work":
	tab.name="订单操作费";
	tab.caption="订单操作费";
	tab.path="com.jiuyescm.bms.bill.receive.lookup.OutStockLookUp.d";
	break;
	case "wh_material_use":
	tab.name="耗材报价";
	tab.caption="耗材报价";
	tab.path="com.jiuyescm.bms.bill.receive.lookup.MaterialLookUp.d";
	break;
	case "wh_value_add_subject":
	tab.name="增值报价";
	tab.caption="增值报价";
	tab.path="com.jiuyescm.bms.bill.receive.lookup.OtherStorageLookUp.d";
	break;
}
if(tab.path){
	 with (view.get("#tabControl")){
	 	var currentTab = getTab(tab.name);
        if (currentTab) {
            tab = currentTab;
        } else {
            // 获取ID为tabControl的对象，并添加一个新的Tab
            // 设定ID为tabControl的对象的当前Tab为新创建的Tab
            tab = addTab(tab);
        }
        // 设定当前的Tab为制定的tab
        set("currentTab", tab);
	 }
}

DataColumn.ClientEvent:
var detailFrame= window.parent.$id("detailFrame").objects[0];
var detailData=detailFrame.get("userData");
var billsatus=
jQuery(arg.dom).empty();
var status=arg.data.get("status");
var timestamp = Date.parse(new Date());
switch(status){
	case "DELETE"://已删除
	createDeleteLink();
	break;
	case "GENERATED"://已生成
	createGeneratedLink();
	break;
	case "UPDATE"://重新生成
	createUpdateLink();
	break;
	case "CONFIRM":
	createConfirmLink();
	break;
}



function flush(){
	var myframe= window.parent.$id("detailFrame").objects[0];
	if(!isNullOrEmpty(myframe)){
		var userData=myframe.get("userData");
		if(userData){
			var billno=userData;
			var ds=view.get("#dsWarehouseGroup");
			ds.set("parameter",billno).flush();//不能用异步刷新
			var datas=view.get("#dsWarehouseGroup.data");
			var amount=0;
			datas.each(function(item){
				amount=addNum(amount,item.get("amount"));
			});
			var tab=view.get("#storagedispatchTab");
			tab.set("caption","仓储配送( "+amount+" )");
			//异常费用 
		}else{
			dorado.MessageBox.alert("账单编号为空");
		}
	}
}

function createConfirmLink(){
	jQuery(arg.dom).xCreate(
	{
		    tagName: "A",
		    href: "#",
		    content:"费用详情",
			style:"margin-left:10px;",
		    onclick: function() {
				var subjectName=arg.data.get("subjectName");
				var subjectCode=arg.data.get("subjectCode");
				var status=arg.data.get("status");
				var billNo=arg.data.get("billNo");
				var warehouseCode=arg.data.get("warehouseCode");
				var customerId=arg.data.get("customerId");
				var parameter={
					billno:billNo,
					warehouseCode:warehouseCode,
					status:status,
					subjectCode:subjectCode,
					feesType:"STORAGE",
					customerId:customerId,
					customerName:detailData.customerName,
					startTime:detailData.startTime,
					endTime:detailData.endTime
				};
				var iframe=view.get("#myframe");
				iframe.set("userData",parameter);
				switch(subjectCode){
					case "wh_product_storage"://商品存储费
					case "wh_product_pallet_storage":
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.ProductStorageLookUp.d?timestamp="+timestamp);
					break;
					case "wh_material_storage"://耗材存储费
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.PackStorageLookUp.d?timestamp="+timestamp);
					break;
					case "wh_b2c_work"://订单操作费
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.OutStockLookUp.d?timestamp="+timestamp);
					break;
					case "wh_material_use"://耗材报价
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.MaterialLookUp.d?timestamp="+timestamp);
					break;
					case "wh_value_add_subject"://耗材报价
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.OtherStorageLookUp.d?timestamp="+timestamp);
					break;
					case "Abnormal_Storage"://理赔费用
					iframe.set("path","com.jiuyescm.bms.bill.receive.lookup.AbnormalLookUp.d?timestamp="+timestamp);
					break;
					default:
					iframe.set("path","");
					break;
				}
				var dialog=view.get("#dialogAcountDetail");
				dialog.set("caption","【"+subjectName+"】费用明细");
				dialog.show();
				
	    	}
		}
	);
}
