Button.ClientEvent:
var action=view.get("#discountAction");

var entity=view.get("#dsBillSubjectInfo.data").current;
var discountType=entity.get("discountType");

if(discountType=='DISCOUNT_RATIO'){
	var discountRate=entity.get("discountRate");
	if(isNullOrEmpty(discountRate)){
		dorado.MessageBox.alert("折扣率不能为空！");
		return;
	}	
	if(parseFloat(discountRate)>100){
		dorado.MessageBox.alert("折扣率不能超过百分百！");
		return;
	}
}

action.execute(function(){
	dorado.MessageBox.alert("操作成功");
	view.get("#dsWarehouseGroup").flush();
	view.get("#dialogDiscount").hide();
});

RadioGroup.ClientEvent:
var currentValue= self._value;
if(currentValue == 'DISCOUNT_MONEY'){
	view.get("#discountAmount").set("visible", true);
	view.get("#discountAmount").set("readOnly", false);
	view.get("#discountRate").set("visible", false);
	view.get("#te").set("visible", false);
	var data = this.get("#dsBillSubjectInfo").getData("#");
	data.set({
		discountType:'DISCOUNT_MONEY',
		discountRate:''
	});
}else if (currentValue == 'DISCOUNT_RATIO'){
	view.get("#discountAmount").set("readOnly", true);
	view.get("#discountRate").set("visible", true);
	view.get("#te").set("visible", true);
	var data = this.get("#dsBillSubjectInfo").getData("#");
	data.set({
		discountType:'DISCOUNT_RATIO',
	});
}

AutoFormElement.ClientEvent:
var data=view.get("#dsBillSubjectInfo.data").current;
var discountRate=data.get("discountRate");
if(discountRate!=null&&discountRate!=""){
	var totalAmount=data.get("totalAmount");
	var derateAmount=data.get("derateAmount");
	var discountAmount=accSub(totalAmount,(totalAmount*discountRate/100).toFixed(2));
	var receiptAmount=accSub(totalAmount,addNum(derateAmount,discountAmount));
	data.set("receiptAmount",receiptAmount);
	data.set("discountAmount",discountAmount);
}

var data=view.get("#dsBillSubjectInfo.data").current;
var discountAmount=data.get("discountAmount");
if(discountAmount!=null&&discountAmount!=""){
	var totalAmount=data.get("totalAmount");
	var derateAmount=data.get("derateAmount");
	var receiptAmount=accSub(totalAmount,addNum(derateAmount,discountAmount));	
	data.set("receiptAmount",receiptAmount);
}

