	createDeleteLink();
	break;
	case "GENERATED"://已生成
	createGeneratedLink();
	break;
	case "UPDATE":
	createUpdateLink();
	break;
	case "CONFIRM":
	createConfirmLink()
	break;
}

function flush(){
	var myframe= window.parent.$id("detailFrame").objects[0];
	if(!isNullOrEmpty(myframe)){
		var userData=myframe.get("userData");
		if(userData){
			var billno=userData;
			var ds=view.get("#dsWarehouseGroup");
			ds.set("parameter",billno).flush();//不能用异步刷新
			var datas=view.get("#dsWarehouseGroup.data");
			var amount=0;
			datas.each(function(item){
				amount=addNum(amount,item.get("amount"));
			});
			var tab=view.get("#storagedispatchTab");
			tab.set("caption","仓储配送( "+amount+" )");
			//异常费用 
		}else{
			dorado.MessageBox.alert("账单编号为空");
		}
	}
}

