if(selectData.length<1){
	dorado.MessageBox.alert("请首先选择需要重算的数据!");
	return;
}

dorado.MessageBox.confirm("是否确定重算?",function(){
	action.execute(function(result){
		view.id("dialogAddressEdit").hide();
		
		var r="";
		if(result.length>0){
			for(var i=0;i<result.length;i++){
				r=r+result[i]+'\n';
			}	
		}else{
			r="调整重算状态成功";
		}
		
		dorado.MessageBox.alert(r);
		with (view.get("#dsBizDispatchBillEntity")){
			//定义查询条件json结构s
			flushAsync();
		}
		
	});
});

