var validRetryAction = view.get("#validRetryAction");
var retryAction = view.get("#RetryAction");
var datas = view.get("#dsCondition.data").current;

//var merchantId=datas.get("merchantId");
//if(merchantId=='' || merchantId==undefined){
//	dorado.MessageBox.alert("商家不能为空!");
//	return;
//}

validRetryAction.set("parameter",datas);
retryAction.set("parameter",datas);
validRetryAction.execute(function(retMap){
		if(retMap.key == "Over"){
			dorado.MessageBox.alert(retMap.value);
			return;
		}
		if(retMap.key == "Billed"){
			dorado.MessageBox.alert(retMap.value);
			return;
		}
		if(retMap.key == "Error"){
			dorado.MessageBox.alert(retMap.value);
			return;
		}
		if(retMap.key == "Calculated" || retMap.key == "OK"){
			dorado.MessageBox.confirm(retMap.value+" 是否确定重算?",function(){
				retryAction.execute(function(result){
					dorado.MessageBox.alert(result);
					with (view.get("#dsBizDispatchBillEntity")){
						flushAsync();
					}
				});
			});
			return;
		}
});

var condition=view.get("#queyConditionForm.entity");
var cre=condition.get("createTime");
var end=condition.get("endTime");

if(isNullOrEmpty(cre)){
	dorado.MessageBox.alert("开始时间不能为空!");
	return;
}
if(isNullOrEmpty(end)){
	dorado.MessageBox.alert("结束时间不能为空!");
	return;
}
if(!isNullOrEmpty(cre) && !isNullOrEmpty(end)){
 	if(cre.getTime()>end.getTime()){
		dorado.MessageBox.alert("起始时间不能大于结束时间！");
		return;
	}
}
var m=getDays(cre,end);
if(m>31){
	dorado.MessageBox.alert("不能查询时间间隔大于1个月的数据!");
	return;
}

view.get("#bizDownAction").set("parameter",condition).execute(
	function(result){
		dorado.MessageBox.alert(result);
	}
);

with (view.get("#dsFileExportTaskList")){
