AutoForm.ClientEvent:
	var nowdays = new Date();
    var year = nowdays.getFullYear();
    var month = nowdays.getMonth();
    if(month==0)
    {
        month=12;
        year=year-1;
    }
    if (month < 10) {
        month = "0" + month;
    }
    var firstDay = year + "-" + month + "-" + "01 00:00:00";//上个月的第一天
    var myDate = new Date(year, month, 0);
    var lastDay = year + "-" + month + "-" + myDate.getDate()+" 23:59:59";//上个月的最后一天
	view.get("#queryForm.entity").set({
		createTime:firstDay,
		createEndTime:lastDay
	});

DataColumn.ClientEvent:
jQuery(arg.dom).empty();
jQuery(arg.dom).xCreate({
	tagName: "A",
	href: "#",
	content:dateToString(arg.data.get("curTime"))+"",
	onclick:function(){
		var data = view.get("#dsBizPalletInfoEntity.data").current;
		var curTime = dateToString(arg.data.get("curTime"));
		var warehouseCode = data.get("warehouseCode");
		var customerId = data.get("customerId");
		var temperatureTypeCode = data.get("temperatureTypeCode");
		var param="?curTime="+curTime+"&warehouseCode="+warehouseCode+"&customerId="+customerId+"&temperatureTypeCode="+temperatureTypeCode;
		window.top.openUrlInFrameTab("com.jiuyescm.bms.biz.pallet.view.PalletDetail.d"+param, "托数明细", "", true);
	}
});

// 将Date格式转成yyyy-MM-dd的字符串格式
function dateToString(date){ 
  var year = date.getFullYear(); 
  var month =(date.getMonth() + 1).toString(); 
  var day = (date.getDate()).toString();  
  if (month.length == 1) { 
      month = "0" + month; 
  } 
  if (day.length == 1) { 
      day = "0" + day; 
  }
  var dateTime = year + "-" + month + "-" + day;
  return dateTime; 
}

