//导入Excel之后，在这里拿到上传成功之后的返回值 例如java方法里返回Map值这里获得数据后会映射成json
//注意这里
var data= arg.returnValue;
if(data.succ!=null){
	view.get("#uprogressLabel").set("text","导入成功！");
	dorado.MessageBox.alert("导入成功!",function(){
		view.get("#updateWayBillDialog").hide();	
		var data = view.get("#dsBizPalletInfoEntityQuery.data").current;
		with (view.get("#dsBizPalletInfoEntity")){
			set("parameter", data);
			flushAsync();
		}
	});
}
if(data.error!=null){
	 var dataSet=view.get("#dsErrorMessageVo");
     dataSet.setData(data.error);
	 view.get("#uprogressLabel").set("text","导入失败！");
     view.get("#errorMsgDialog").show();
	 return;
}

$("#d_uprogressLabel").show();
$("#d_uimportProgressBar").show();
view.get("#uprogressLabel").set("text", "请不要关闭当前页面，系统正在处理数据，请耐心等待...");
view.get("#uimportProgressBar").set("value", 0);
var action = view.get("#ugetProgress");
var setAction = view.get("#usetProgress");
var oldVal = 0;
var getProgress = function() {
	action.execute(function (res) {
		
		if (oldVal != res) {
			for (oldVal; oldVal <= res; oldVal++) {
				view.get("#uimportProgressBar").set("value", res/10);
			}
		}
		oldVal = res;
		if (res == 999) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#uimportCloseBtn").set("disabled",false);
			view.get("#uprogressLabel").set("text","导入失败！");
		}
		if (res == 1000) {
			clearInterval(progressJob);
			//设置为 0
			setAction.execute(function (res) {  });
			view.get("#uimportCloseBtn").set("disabled",false);
			view.get("#uprogressLabel").set("text","导入完成！");
			
			var data = view.get("#dsBizPalletInfoEntityQuery.data").current;
				with (view.get("#dsBizPalletInfoEntity")){
					set("parameter", data);
					flushAsync();
				}
		}
	});
};

var progressJob = setInterval(getProgress, 500);

