$("#d_progressLabel").show();
$("#d_importProgressBar").show();
view.get("#progressLabel").set("text", "请不要关闭当前页面，系统正在处理数据，请耐心等待...");
view.get("#importProgressBar").set("value", 0);
var oldVal = 0;
var getProgress = function() {
	var action = view.get("#getProgress");
	action.execute(function (res) {
		switch(res){
			case 0:
			view.get("#progressLabel").set("text", "开始处理Excel");
			view.get("#importProgressBar").set("value", 10);
			break;
			case 1:
			view.get("#progressLabel").set("text", "验证模板........");
			view.get("#importProgressBar").set("value", 20);
			break;
			case 2:
			view.get("#progressLabel").set("text", "读取Excel。。。。。。。");
			view.get("#importProgressBar").set("value", 40);
			break;
			case 3:
			view.get("#progressLabel").set("text", "验证数据........");
			view.get("#importProgressBar").set("value", 60);
			break;
			case 4:
			view.get("#progressLabel").set("text", "保存数据........");
			view.get("#importProgressBar").set("value", 80);
			break;
			case 5:
			clearInterval(progressJob);
			resetProgress();
			view.get("#importCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text", "保存成功........");
			view.get("#importProgressBar").set("value", 100);
			break;
			case 6:
			clearInterval(progressJob);
			resetProgress();
			view.get("#importCloseBtn").set("disabled",false);
			view.get("#progressLabel").set("text", "导入失败........");
			view.get("#importProgressBar").set("value", 99);
			break;
			default:
			view.get("#progressLabel").set("text", res);
			break;
		}
	});
};
function resetProgress(){
	view.get("#ajaxsetProgress").execute();	
}
var progressJob = setInterval(getProgress, 500);
