View.ClientEvent:
var customerId = GetQueryString("customerId");
var startTime = GetQueryString("createTime");
var endTime = GetQueryString("createEndTime");
//var subjectCode = GetQueryString("subjectCode");
var isCalculated = GetQueryString("isCalculated");

if(customerId == undefined || customerId == null){
	return;
}

var data = view.get("#dsCondition.data").current;
data.set("customerId",customerId);
data.set("startTime",startTime);
data.set("endTime",endTime);
//data.set("subjectCode",subjectCode);
data.set("isCalculated",isCalculated);

var dsMaterialList= view.get("#dsMaterialList");
with (dsMaterialList) {
	dsMaterialList.clear();
	set("parameter", data);
	flush();
} 


function GetQueryString(name){
    var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)"); 
    var r = window.location.search.substr(1).match(reg);
    if(r!=null)return  unescape(r[2]); return null;
}

ToolBarButton.ClientEvent:
var data = view.get("#dsCondition.data").current;
if(isNullOrEmpty(data.get("customerId")) && isNullOrEmpty(data.get("warehouseCode"))){
	dorado.MessageBox.alert("商家或仓库必选一个!");
	return;
}
if(isNullOrEmpty(data.get("startTime"))){
	dorado.MessageBox.alert("创建时间不能为空!");
	return;
}
if(isNullOrEmpty(data.get("endTime"))){
	dorado.MessageBox.alert("结束时间不能为空!");
	return;
}
	
if(data.get("startTime")>data.get("endTime")){
	dorado.MessageBox.alert("开始时间不能大于结束时间!");
	return;
} 
var m=getDays(data.get("startTime"),data.get("endTime"));
if(m>62){
	dorado.MessageBox.alert("不能查询时间间隔大于2个月的数据!");
	return;
}
with (view.get("#dsMaterialList")){
	set("parameter", data);
	flushAsync();
}	
function getDays(strDateStart,strDateEnd){
   var iDays = parseInt(Math.abs(strDateEnd - strDateStart ) / 1000 / 60 / 60 /24)//把相差的毫秒数转换为天数
   return iDays ;
}

