MenuButton.ClientEvent:
var data = view.get("#dsCondition.data").current;
if(data){
	if(isNullOrEmpty(data.get("startTime"))){
		dorado.MessageBox.alert("创建时间不能为空!");
		return;
	}
	if(isNullOrEmpty(data.get("endTime"))){
		dorado.MessageBox.alert("结束时间不能为空!");
		return;
	}
	var dataSet=view.get("#dsMaterialList.data");
 	var index=0;
	dataSet.each(function(item){
		index++;
	})
	if(index==0){
		dorado.MessageBox.alert("无数据汇总统计");
		return;
	}
	with (view.get("#dsMaterialWarehouseGroupCountList")){
		set("parameter", data);
		flush();
	}
	with (view.get("#dsMaterialPriceGroupCountList")){
		set("parameter", data);
		flush();
	}
	view.get("#exportF").set("visible",false);
	view.get("#groupCountDialog").show();
}else{
	dorado.MessageBox.alert("请选择需要统计的筛选条件!");
}

//定义一个函数，判断参数是否为空
function isNullOrEmpty(e) {
    if (e == null || e == undefined || e == "") {
        return true;
    } else {
        return false;
    }
}

ToolBarButton.ClientEvent:
var validRetryAction = view.get("#validRetryAction");
var retryAction = view.get("#RetryAction");
var datas = view.get("#dsCondition.data").current;
if(datas.get("customerId")==""||datas.get("customerId")==null)
{
	dorado.MessageBox.alert("商家不能为空!");
	return;
}
validRetryAction.set("parameter",datas);
retryAction.set("parameter",datas);
validRetryAction.execute(function(retMap){
		if(retMap.key == "Billed"){
			dorado.MessageBox.alert(retMap.value);
			return;
		}
		if(retMap.key == "Error"){
			dorado.MessageBox.alert(retMap.value);
			return;
		}
		if(retMap.key == "Calculated" || retMap.key == "OK"){
			dorado.MessageBox.confirm(retMap.value+" 是否确定重算?",function(){
				retryAction.execute(function(result){
					dorado.MessageBox.alert(result);
					with (view.get("#dsMaterialList")){
						flushAsync();
					}
				});
			});
			return;
		}
});

