AutoForm.ClientEvent:
	var nowdays = new Date();
    var year = nowdays.getFullYear();
    var month = nowdays.getMonth();
    if(month==0)
    {
        month=12;
        year=year-1;
    }
    if (month < 10) {
        month = "0" + month;
    }
    var firstDay = year + "-" + month + "-" + "01 00:00:00";//上个月的第一天
    var myDate = new Date(year, month, 0);
    var lastDay = year + "-" + month + "-" + myDate.getDate()+" 23:59:59";//上个月的最后一天
	view.get("#queryConditionFrm.entity").set({
		startTime:firstDay,
		endTime:lastDay
	});

Button.ClientEvent:
var entity = view.get("#adjustNumFrm").get("entity");

var adjustNum=entity.get("adjustNum");
var num=entity.get("num");
if(undefined==adjustNum || adjustNum ==''){
	dorado.MessageBox.alert("请填写调整数量!");
	return;
}
if(isNaN(adjustNum)){
	dorado.MessageBox.alert("请输入数字");
	return false;
}
if((parseFloat(adjustNum) + parseFloat(num)) <= 0){
	dorado.MessageBox.alert("调整后的数量为"+(num + adjustNum)+",不能小于等于0");
	return false;
}

view.get("#updateAction").execute(
	function(result){
		if(result.code == "SUCCESS"){
			view.get("#adjustNumDialog").hide();
			dorado.MessageBox.alert("修改成功");
			var data = view.get("#dsCondition.data").current;
			with (view.get("#dsMaterialList")){
				set("parameter", data);
				flushAsync();
			}
		}else{
			dorado.MessageBox.alert(result.data);
		}
	}
);

dorado.MessageBox.confirm("是否确定取消此操作？",
	function(){
		view.id("dsMaterialList").getData("#").cancel();
		view.id("adjustNumDialog").hide();
		var data = view.get("#dsCondition.data").current;
		with (view.get("#dsMaterialList")){
			set("parameter", data);
			flushAsync();
		}
	}
);

