			clearInterval(progressJob);
			resetProgress();
			view.get("#importCloseBtn2").set("disabled",false);
			view.get("#progressLabel2").set("text", "导入失败........");
			view.get("#importProgressBar2").set("value", 99);
			break;
			default:
			view.get("#progressLabel2").set("text", res);
			break;
		}
	});
};
function resetProgress(){
	view.get("#ajaxsetProgress2").execute();	
}
var progressJob = setInterval(getProgress, 500);
