var data=view.get("#dsFileExportTaskList.data").current;
if(!data){
	dorado.MessageBox.alert("请选择要删除的记录!");
	return;
}
var taskId = data.get("taskId");
if(isNullOrEmpty(taskId)){
	dorado.MessageBox.alert("请选择要删除的记录");
	return;
}

var action=view.get("#delExportTaskAction");
dorado.MessageBox.confirm("确定删除["+data.get("taskName")+"]的记录?",function(){
	action.set("parameter",data).execute(function(){
		dorado.MessageBox.alert("删除成功!");
		with (view.get("#dsFileExportTaskList")){
			//定义查询条件json结构
			var queryJson={
				taskType:'pro_pallet'
			};
			set("parameter", queryJson);
			flushAsync();
		}
	});
})

