Button.ClientEvent:
var entity = view.get("#editFormProductPalletStorage").get("entity");
var palletNum = entity.get("palletNum");
//dorado.MessageBox.alert(palletNum);
if(undefined==palletNum || palletNum==''){
	dorado.MessageBox.alert("请填写托数!");
	return;
}

if (isNaN(palletNum)) {
	dorado.MessageBox.alert("托数必须为数字!");
	return;
}
if (palletNum <=0) {
	dorado.MessageBox.alert("托数必须大于0!");
	return;
}

view.id("updateProductPalletStorageAction").execute(
	function(result){
		view.id("editDialogProductPalletStorage").hide();
		dorado.MessageBox.alert(result);
		
		var tmpDataSet=view.get("#dataSetProductPalletStorage");
		var tmpFormCondition=view.get("#queryFormProductPalletStorage");
		tmpFormCondition.get("entity").set("delFlag","0");
		tmpDataSet.set("parameter", tmpFormCondition.get("entity")).flushAsync();
	}
);

dorado.MessageBox.confirm("是否确定取消此操作？",
	function(){
		view.id("dataSetProductPalletStorage").getData("#").cancel();
		view.id("editDialogProductPalletStorage").hide();
	}
);

